import React,{useContext} from 'react'
import { useNavigate } from 'react-router-dom';
import flightContent from "../context/FlightContext";

const Flightitem = (props) => {
    const {flight} = props
    console.log(flight._id)
    const content = useContext(flightContent)
    const { setSelectedflight ,setSearch} = content 
    const host = "http://localhost:4000"
    var navigate = useNavigate()


    const bookFlight = async (flightid) => {
        const response = await fetch(`${host}/api/v1/flights?_id=${flightid}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          }
        });
        const json = await response.json()
        console.log(json.flights)
        setSelectedflight(json.flights)
        setSearch('false')
        navigate('/passengerdetails')
      }
  return (
    <div  className='flight-item'>
        <div className='airways-details'>
            <div className='details-item'>{flight.flightname}</div>
            <div className='details-item'>{flight.flightnumber}</div>  
        </div>
        <div className='td-details'>
            <div>
                <div className='details-item'>{flight.departuretime}</div>
                <div className='details-item'>{flight.from}</div>
            </div>
            <div className='d-flex align-items-center'>
                <div className='journey-line'></div>
            </div>
            <div>
                <div className='details-item'>{flight.arrivaltime}</div>
                <div className='details-item'>{flight.to}</div>
            </div>
        </div>
        <div className='d-flex align-items-center'>
            <div className='price-details'>&#8377;{flight.price}</div>
        </div>
        <div className='d-flex align-items-center'>
            <button onClick={() => bookFlight(flight._id)} className='book-btn'>Book</button>
        </div>
    </div>
  )
}

export default Flightitem
