import React, { useState } from "react";

const Passengercount = () => {
  const [adultCounter, setadultCounter] = useState(1);
  const [childCounter, setchildCounter] = useState(0);
  const [infantCounter, setinfantCounter] = useState(0);
  const [passCount, setPassCount] = useState(1);
  const [pclass, setPclass] = useState("Ecomony");
  return (
    <>
    <div className="pass-class-con">
      <div className="pass-detail-con">
        <div className="p-d-heading">Passenger</div>
        <div className="d-flex justify-content-between">
          <div>Adult</div>
          <div className="d-flex">
            <div
              className="p-counter"
              onClick={() => {
                if (passCount > 1) {
                  setadultCounter((adultCounter) => adultCounter - 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter - 1
                  );
                }
              }}
            >
              -
            </div>
            <div className="p-value p-a-value">{adultCounter}</div>
            <div
              className="p-counter"
              onClick={() => {
                if (passCount < 9) {
                  console.log(passCount);
                  setadultCounter((adultCounter) => adultCounter + 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter + 1
                  );
                }
              }}
            >
              +
            </div>
          </div>
        </div>
        <div className="d-flex justify-content-between">
          <div>Child</div>
          <div className="d-flex">
            <div
              className="p-counter"
              onClick={() => {
                if (passCount > 1) {
                  if (childCounter >= 1) {
                    setchildCounter((childCounter) => childCounter - 1);
                  }
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter - 1
                  );
                }
              }}
            >
              -
            </div>
            <div className="p-value p-c-value">{childCounter}</div>
            <div
              className="p-counter"
              onClick={() => {
                if (passCount < 9) {
                  setchildCounter((childCounter) => childCounter + 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter + 1
                  );
                }
              }}
            >
              +
            </div>
          </div>
        </div>
        <div className="d-flex justify-content-between mb-3">
          <div>Infant</div>
          <div className="d-flex">
            <div
              className="p-counter"
              onClick={() => {
                if (passCount > 1) {
                  if (infantCounter >= 1) {
                    setinfantCounter((infantCounter) => infantCounter - 1);
                  }
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter - 1
                  );
                }
              }}
            >
              -
            </div>
            <div className="p-value p-i-value">{infantCounter}</div>
            <div
              className="p-counter"
              onClick={() => {
                if (passCount < 9) {
                  setinfantCounter((infantCounter) => infantCounter + 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter + 1
                  );
                }
              }}
            >
              +
            </div>
          </div>
        </div>
        <div className="sec-sep-line"></div>
        <div className="p-d-heading mt-2">Class</div>
        <div className="d-flex justify-content-between">
          <label htmlFor="vehicle2">Economy</label>
          <input type="radio" id="economy" name="class" value="economy" />
        </div>
        <div className="d-flex justify-content-between">
          <label htmlFor="vehicle3">Premieum</label>
          <input type="radio" id="premeium" name="class" value="premeium" />
        </div>
      </div>
    </div>
    <div class="dropdown">
  <button class="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
  <div className="title">Passenger / Class</div>
    <div className="pc-displayvalue">
      {passCount},{pclass}
    </div>
  </button>
  <div class="dropdown-menu">
  <div className="pass-class-con">
      <div className="pass-detail-con">
        <div className="p-d-heading">Passenger</div>
        <div className="d-flex justify-content-between">
          <div>Adult</div>
          <div className="d-flex">
            <div
              className="p-counter"
              onClick={() => {
                if (passCount > 1) {
                  setadultCounter((adultCounter) => adultCounter - 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter - 1
                  );
                }
              }}
            >
              -
            </div>
            <div className="p-value p-a-value">{adultCounter}</div>
            <div
              className="p-counter"
              onClick={() => {
                if (passCount < 9) {
                  console.log(passCount);
                  setadultCounter((adultCounter) => adultCounter + 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter + 1
                  );
                }
              }}
            >
              +
            </div>
          </div>
        </div>
        <div className="d-flex justify-content-between">
          <div>Child</div>
          <div className="d-flex">
            <div
              className="p-counter"
              onClick={() => {
                if (passCount > 1) {
                  if (childCounter >= 1) {
                    setchildCounter((childCounter) => childCounter - 1);
                  }
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter - 1
                  );
                }
              }}
            >
              -
            </div>
            <div className="p-value p-c-value">{childCounter}</div>
            <div
              className="p-counter"
              onClick={() => {
                if (passCount < 9) {
                  setchildCounter((childCounter) => childCounter + 1);
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter + 1
                  );
                }
              }}
            >
              +
            </div>
          </div>
        </div>
        <div className="d-flex justify-content-between mb-3">
          <div>Infant</div>
          <div className="d-flex">
            <div
              className="p-counter"
              onClick={() => {
                if (passCount > 1) {
                  if (infantCounter >= 1) {
                    setinfantCounter((infantCounter) => infantCounter - 1);
                  }
                  setPassCount(
                    (passCount) =>
                      adultCounter + childCounter + infantCounter - 1
                  );
                }
              }}
            >
              -
            </div>
            <div className="p-value p-i-value">{infantCounter}</div>
            <div
              className="p-counter"
              onClick={() => {
                if (passCount < 9) {
