import React,{useContext,useEffect} from 'react'
import Flightitem from "./Flightitem";
import flightContent from "../context/FlightContext";
import '../styles/Search.css'

const OnewaySearch = () => {
    const content = useContext(flightContent)
    const {flights, getFlights, setFlights, setSearch, departure, setDeparture, arrival, setArrival, settripValue} = content 

    useEffect(() => {
        setSearch('true')
        setDeparture(localStorage.getItem('departure'))
        setArrival(localStorage.getItem('destination'))
        settripValue(localStorage.getItem('triptype'))
        const controller = new AbortController();
        const signal = controller.signal;
        getFlights(localStorage.getItem('departurecode'),localStorage.getItem('destinationcode'),signal)
        .then((data)=>{setFlights(data.flights)})
        .catch(err=>{
            if(err.name === "AbortError"){
            console.log('aborted')
            }
        })
        return () => {
            controller.abort();
        }
    }, [])

  return (
    <div className="flight-item-con">
        <div className='flight-item-title'>Select Flight from {departure.slice(0,departure.indexOf('-'))} to {arrival.slice(0,arrival.indexOf('-'))}</div>
        {flights.map((flight) => {
            return <Flightitem key={flight._id} flight={flight}/>
        })}
    </div>
  )
}

export default OnewaySearch
