import React,{useContext,useEffect,useRef} from 'react'
import Flightitem from "./Flightitem";
import flightContent from "../context/FlightContext";



const Searchflights = () => {
    const content = useContext(flightContent)
    const {flights, getFlights, setFlights, setSearch} = content 

    useEffect(() => {
      setSearch('true')
      const controller = new AbortController();
      const signal = controller.signal;
      getFlights(localStorage.getItem('departure'),localStorage.getItem('destination'),signal)
      .then((data)=>{setFlights(data.flights)})
      .catch(err=>{
        if(err.name === "AbortError"){
          console.log('aborted')
        }
      })
      return () => {
        controller.abort();
      }
    }, [])
    const ref = useRef(null)
    const handleEvent = () => {
      // ref.current.click();
      window.alert('hi')
    };

    useEffect(() => {
      window.addEventListener("popstate", handleEvent);
      return () => window.removeEventListener("popstate", handleEvent);
    });

    // const confirmalert = () => {
    //     // e.preventDefault();
    //     ref.current.click();
    // }
    // window.onpopstate = confirmalert
  return (
    <div className="flight-item-con">
            <button ref={ref} type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
        </button>

<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
    {flights.map((flight) => {
        return <Flightitem key={flight._id} flight={flight}/>
    })}
  </div>
  )
}

export default Searchflights
