import React,{useContext,useEffect,useState} from 'react'
import Flightitem from "./Flightitem";
import flightContent from "../context/FlightContext";
import { useNavigate } from 'react-router-dom';


const ReturnSearch = () => {
    const [showElement,setShowElement] = useState(true)
    const content = useContext(flightContent)
    const {flights, getFlights, setFlights, setSearch, departure, setDeparture, arrival, setArrival, tripValue, settripValue, rflights, setRflights, tripFlights , setTripFlights} = content 
    const navigate = useNavigate()
    useEffect(() => {
        setSearch('true')
        setDeparture(localStorage.getItem('departure'))
        setArrival(localStorage.getItem('destination'))
        settripValue(localStorage.getItem('triptype'))
        const controller = new AbortController();
        const signal = controller.signal;
        getFlights(localStorage.getItem('destinationcode'),localStorage.getItem('departurecode'),signal)
        .then((data)=>{setRflights(data.flights)})
        .catch(err=>{
            if(err.name === "AbortError"){
            console.log('aborted')
            }
        })
        return () => {
            controller.abort();
        }
    }, [])
    const undoSelection = () => {
        setTripFlights('')
        navigate('/owsearch')
    }
    useEffect(()=>{
        setTimeout(function() {
          setShowElement(false)
             }, 3000);
           },
       [])

  return (
    <div className="flight-item-con">
        <div className='flight-item-title'>Select Flight from {arrival} to {departure}</div>
        {rflights.map((flight) => {
            return <Flightitem key={flight._id} flight={flight}/>
        })}
        <div className='d-flex justify-content-center'>
        {showElement?<div className='undo-alert-con'>
                        <div>Departure selected</div>
                        <div onClick={undoSelection} >Undo selection</div>
                    </div>
                    :<></>
        }
        </div>
    </div>
  )
}

export default ReturnSearch
