import React,{useState,useContext,useEffect} from "react";
import flightContent from "../context/FlightContext";
import { useNavigate } from "react-router-dom";
import Alert from './Alert';
import 'react-dates/initialize';
import moment from 'moment'
import { DateRangePicker,SingleDatePicker } from 'react-dates';
import 'react-dates/lib/css/_datepicker.css';
import "../styles/Home.css";


const Home = () => {

  var navigate = useNavigate()
  const [alert,setAlert] = useState({})

  // Departure-arrival
  const content = useContext(flightContent)
  const { departure, setDeparture, setArrival, arrival, filter, 
          filterDest, onChangeDeparture, onChangeArrival , departDate, 
          setdepartDate, returnDate, setreturnDate, setSearch, tripValue, 
          settripValue, adultCounter, setadultCounter, childCounter,
          setchildCounter, infantCounter, setinfantCounter, passCount,
          setPassCount, pclass, setPclass, setPassarray
        } = content 

  const [placeholder, setPlaceholder] = useState({"from":"dnone","to":"dnone"})
  const [phvalue, setPhvalue]= useState({"from":"From", "to":"To"})
  const [height,setHeight] = useState({"from":"ihide", "to":"ihide"})

  // Search for flights
  const SearchFlights = () => {
    if(departure && arrival && createdAt && pclass){
      const pa = []
      for(let i=0; i<adultCounter; i++){pa.push('Adult')}
      for(let i=0; i<childCounter; i++){pa.push('Child')}
      for(let i=0; i<infantCounter; i++){pa.push('Infant')}
      localStorage.setItem('passarray',JSON.stringify(pa))
      setPassarray(pa)
      localStorage.setItem('departure',departure)
      localStorage.setItem('destination',arrival)
      const depart = departure.split('-')
      const dest = arrival.split('-')
      console.log(depart[1],dest[1])
      localStorage.setItem('departurecode',depart[1])
      localStorage.setItem('destinationcode',dest[1])
      localStorage.setItem('triptype',tripValue)
      localStorage.setItem('passengercount',passCount)
      navigate('/owsearch')
    }
    else{
      if(departure === ''){setAlert({departure:"Departure airport is missing"})}
      if(arrival === ''){setAlert({...alert,arrival:'Destination airport is missing'})}
      if(departDate === ''){setAlert({...alert,date:'Departure date is missing'}) }
      setAlert({...alert,success:true})
    }
  }
  const handleRadioClass = (event) => {
    setPclass(event.target.value)
  } 

  useEffect(() => {
    setSearch(false)
    setDeparture(localStorage.getItem('departure'))
    setArrival(localStorage.getItem('destination'))
  }, [])

// const[flightsearch,setFlightSearch] = useState({startDate:null,endDate:null})

const [dateRange, setdateRange] = useState({
  startDate: null,
  endDate: null
});
const [focus, setFocus] = useState(null);

const { startDate, endDate } = dateRange;
const handleOnDateChange = ( startDate, endDate) =>{
  setdateRange(startDate, endDate);
}


const [calendarFocused, setCalendarFocus] = useState(false);  
const [createdAt, setCreatedAt] = useState(moment());



const onDateChange = (createdAt) => setCreatedAt(createdAt);

const onCalendarFocusChange = (focused) => setCalendarFocus(focused);
  return (
    <>
    <div className="search-flights-con">
      <form className="search-flights-form">
        {alert.success?<Alert alert={alert}/>:<></>}
        <div className="f-q-container  d-flex">
          {/* to and from */}
          <div className="desti-con d-flex">
            {/* <div className="dropdown da-select departure-select">
                <div className='dropdown-toggle da-dropdown-btn departure-drop-btn' data-bs-toggle="dropdown" aria-expanded="false">
                    <div className={`da-placeholder ${placeholder.from}`}>From</div>
                    <div>
                        <input className={`departure-input da-input ${height.from}`} 
                          onFocus={(e)=> {
                              if(!e.target.value){
                                  e.target.placeholder="" 
                                  setPlaceholder({...placeholder,"from":"dblock"})
                                  setHeight({...height,"from":"ishow"})
                              }
                            }
                          } 
                          onBlur={(e)=> {
                                  if(e.target.value === ''){
                                      e.target.placeholder="From" 
                                      setPlaceholder({...placeholder,"from":"dnone"})
                                      setHeight({...height,"from":"ihide"})
                                  }
                              }
                          }  
                          placeholder={phvalue.from} onChange={onChangeDeparture}  value={departure} type="text"  name="from"  required/>
                    </div>
                </div>
              <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
                return  <div key={element.id} className='departure-item' 
                          onClick={(e) => {  
                              e.preventDefault()
                              setDeparture(element.city+'-'+element.id)
                              filterDest('')
                              setPhvalue({...phvalue,"from":""})
                              setPlaceholder({...placeholder,"from":"dblock"})
                              setHeight({...height,"from":"ishow"})
                            }
                          }
                          >
                            <div  className='d-flex justify-content-between align-items-center'>
                                <div className='d-city'>{element.city}</div>
                                <div className='d-code'>{element.id}</div>
                            </div>
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-country'>{element.country}</div>
                                <div className='d-name'>{element.name}</div>
                            </div>
                        </div> }) }
              </div>
            </div> */}
            <div className="dropdown da-select departure-select">
                <div className='dropdown-toggle da-dropdown-btn departure-drop-btn' data-bs-toggle="dropdown" aria-expanded="false">
                    <div className="form-floating">
                    <input  className={`da-input-item form-control `} 
                        type="text"
                        onChange={onChangeDeparture}
                        value={departure}
                        name="from" 
                        id="from" 
                        placeholder="From"
                        onBlur={()=> {
                                // let regex =/^[a-zA-Z]{1,}$/.test(personaldetails.expirydate)
                                // setAlert({...alert,"expirydate":regex})
                                // setInvalid({...invalid,'expirydate':regex?'':'is-invalid'})
                            }
                        }   
                    />
                    <label className='cd-innput-label' htmlFor="from">From</label>
                    </div>
                </div>
              <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
                return  <div key={element.id} className='departure-item' 
                          onClick={(e) => {  
                              e.preventDefault()
                              setDeparture(element.city+'-'+element.id)
                              filterDest('')
                              setPhvalue({...phvalue,"from":""})
                              setPlaceholder({...placeholder,"from":"dblock"})
                              setHeight({...height,"from":"ishow"})
                            }
                          }
                          >
                            <div  className='d-flex justify-content-between align-items-center'>
                                <div className='d-city'>{element.city}</div>
                                <div className='d-code'>{element.id}</div>
                            </div>
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-country'>{element.country}</div>
                                <div className='d-name'>{element.name}</div>
                            </div>
                        </div> }) }
              </div>
            </div>
            {/* <div className="form-floating">
                    <input  className={`cd-input-item form-control ${invalid.expirydate}`} 
                        type="date"
                        onChange={onChangepd} 
                        value={personaldetails.expirydate} 
                        name="expirydate" 
                        id="expirydate" 
                        placeholder="Passport expirydate"
                        onBlur={()=> {
                                let regex =/^[a-zA-Z]{1,}$/.test(personaldetails.expirydate)
                                setAlert({...alert,"expirydate":regex})
                                setInvalid({...invalid,'expirydate':regex?'':'is-invalid'})
                            }
                        }   
                    />
                    <label className='cd-innput-label' htmlFor="expirydate">Passport expirydate</label>
                </div> */}
            <div className="dropdown da-select arrival-select">
                <div className='dropdown-toggle da-dropdown-btn' data-bs-toggle="dropdown" aria-expanded="false">
                    <div className={`da-placeholder ${placeholder.to}`}>To</div>
                    <div>
                    <input className={`departure-input da-input ${height.to}`} 
                          onFocus={(e)=> {
                              if(!e.target.value){
                                  e.target.placeholder="" 
                                  setPlaceholder({...placeholder,"to":"dblock"})
                                  setHeight({...height,"to":"ishow"})
                              }
                            }
                          } 
                          onBlur={(e)=> {
                                  if(e.target.value === ''){
                                      e.target.placeholder="To"
                                      setPlaceholder({...placeholder,"to":"dnone"})
                                      setHeight({...height,"to":"ihide"})
                                  }
                              }
                          }  
                          placeholder={phvalue.to} onChange={onChangeArrival} type="text" value={arrival}  name="to" required />
                    </div>
                </div>
              <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
                return  <div key={element.id} className='departure-item' onClick={(e) => {  
                            e.preventDefault()
                            setArrival(element.city+'-'+element.id)
                            filterDest('')
                            setPhvalue({...phvalue,"to":""})
                            setPlaceholder({...placeholder,"to":"dblock"})
                            setHeight({...height,"to":"ishow"})
                            }} >
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-city'>{element.city}</div>
                                <div className='d-code'>{element.id}</div>
                            </div>
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-country'>{element.country}</div>
                                <div className='d-name'>{element.name}</div>
                            </div>
                        </div> }) }
              </div>
            </div>
          </div>
          {/* Trip category */}
          <div className="dropdown trip-details-con">
            <div className="trip-details-btn dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                <div className="trip-placeholder">Trip</div>
                <div className="trip-displayvalue">{tripValue}</div>
            </div>
            <div className="dropdown-menu trip-select-con">
                <div className="trip-item" onClick={()=> settripValue('One way')}>One-way</div>
                <div className="trip-item" onClick={()=> settripValue('Return')}>Return</div>
            </div>
          </div>
          {/* Trip dates */}
          {/* <div className="date-select-con d-flex">
            <div className="depart date-select-item">
                <div className="date-placeholder">Depart</div>
                <input className="date-displayvalue departure" value={departDate} onChange={(e) => setdepartDate(e.target.value)} type="date" id="departuredate" name="departuredate"></input>
            </div>
            {tripValue === 'Return'? <div className="return date-select-item">
              <div className="date-placeholder">Return</div>
              <input className="date-displayvalue departure" value={returnDate} onChange={(e) => setreturnDate(e.target.value)} type="date" id="returndate" name="returndate"></input>
            </div>:<div></div>}
          </div> */}
          <div className="date-select-con d-flex">
          {tripValue === 'One way'
            ?
            <div className="depart date-select-item-d">
              <div className="date-placeholder">Depart</div>
              <SingleDatePicker
                date={createdAt} 
                onDateChange={onDateChange} 
                focused={calendarFocused.focused} 
                onFocusChange={onCalendarFocusChange}
                id='#123'
                displayFormat="D MMM YYYY"
                numberOfMonths={1}
              />
            </div>
            :
            <div className="return date-select-item-r">
              <div className="d-flex justify-content-between">
                <div className="date-placeholder">Depart</div>
                <div className="date-placeholder dp-r">Return</div>
              </div>
              <DateRangePicker
                startDatePlaceholderText="Depart"
                startDate={startDate}
                onDatesChange={handleOnDateChange}
                endDatePlaceholderText="Return"
                endDate={endDate}
                numberOfMonths={1}
                displayFormat="D MMM YYYY"
                focusedInput={focus}
                onFocusChange={focus => setFocus(focus)}
                startDateId="startDateMookh"
                endDateId="endDateMookh"
                minimumNights={0}
              />
            </div>
            }
            </div>
           {/* showClearDates={true} */}
          {/* Passenger Count */}
          <div className="dropdown passengercount-con">
            <div className="dropdown-toggle pc-dropdown-btn" data-bs-toggle="dropdown" aria-expanded="false" data-bs-auto-close="outside">
              <div className="pc-placeholder">Passenger / Class</div>
              <div className="pc-displayvalue">
                {passCount} Passenger, {pclass}
              </div>
            </div>
            <div className="dropdown-menu pass-class-con">
              <div className="pass-detail-con">
                <div className="p-d-heading">Passenger</div>
                <div className="d-flex justify-content-between">
                  <div className="p-holder">Adult (12years+)</div>
                  <div className="d-flex counter-con">
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount > 1) {
                          setadultCounter((adultCounter) => adultCounter - 1);
                          setPassCount((passCount) => passCount - 1);
                        }
                      }}> -
                    </div>
                    <div className="p-value p-a-value">{adultCounter}</div>
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount < 9) {
                          setadultCounter((adultCounter) => adultCounter + 1);
                          setPassCount((passCount) => passCount + 1);
                        }
                      }}>
                      +
                    </div>
                  </div>
                </div>
                <div className="d-flex justify-content-between">
                  <div className="p-holder">Child (2-11 years)</div>
                  <div className="d-flex counter-con">
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount > 1 && childCounter > 0) {
                          setchildCounter((childCounter) => childCounter - 1);
                          setPassCount((passCount) => passCount - 1);
                        }
                      }}>
                      -
                    </div>
                    <div className="p-value p-c-value">{childCounter}</div>
                    <div
                      className="p-counter"
                      onClick={() => {
                        if (passCount < 9) {
                          setchildCounter((childCounter) => childCounter + 1);
                          setPassCount((passCount) => passCount + 1);
                        }
                      }}>
                      +
                    </div>
                  </div>
                </div>
                <div className="d-flex justify-content-between mb-3">
                  <div className="p-holder">Infant (Under 2 years)</div>
                  <div className="d-flex counter-con">
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount > 1 && infantCounter > 0) {
                          setinfantCounter((infantCounter) => infantCounter - 1);
                          setPassCount((passCount) => passCount - 1 );
                        }
                      }}>
                      -
                    </div>
                    <div className="p-value p-i-value">{infantCounter}</div>
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount < 9) {
                          setinfantCounter((infantCounter) => infantCounter + 1);
                          setPassCount((passCount) => passCount + 1
                          );
                        }
                      }}>
                      +
                    </div>
                  </div>
                </div>
                <div className="class-con">
                <div className='select-radio-con'>
                  <div className="form-check select-radio">
                      <input className="form-check-input select-input" onChange={handleRadioClass}  checked={pclass === 'Economy'}  value="Economy" type="radio" name="classOptions" id="ecomomy"/>
                      <label className="form-check-label select-label" htmlFor="male">
                          Economy
                      </label>
                  </div>
                  <div className="form-check select-radio">
                      <input className="form-check-input select-input" onChange={handleRadioClass} type="radio" checked={pclass === 'Premiuem'}  value="Premiuem" name="classOptions" id="premiuem"/>
                      <label className="form-check-label select-label" htmlFor="female">
                          Premiuem
                      </label>
                  </div>
                </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="s-b-con">
          <button type="button" onClick={SearchFlights}className="search-flights-btn">Search Flights</button>
        </div>
      </form>
    </div>
    </>
  )
}

export default Home;
