import React,{useState} from 'react'
import { useNavigate,Link } from 'react-router-dom'
import "../styles/Login.css";
import Alert from './Alert';

const Login = () => {
    let navigate = useNavigate();
    const [credentials, setCredentials] = useState({email: "", password: ""})
    const [placeholder, setPlaceholder] = useState({"uname":"dnone", "pwd":"dnone"})
    const [height,setHeight] = useState({"uname":"ihide", "pwd":"ihide"})
    const [alert,setAlert] = useState({})
  const [alertborder, setAlertborder] = useState({"email":"","password":""})

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:4000/api/v1/auth/login", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({email: credentials.email, password: credentials.password})
        });
        const json = await response.json()
        console.log(json);
        if (json.success){
            // Save the auth token and redirect
            localStorage.setItem('token', json.authtoken); 
            navigate('/');

        }
        else{
            alert("Invalid credentials");
        }
    }

    const onChangelogin = (e) => {
      setCredentials({ ...credentials,[e.target.name]: e.target.value})
    }
  return (
    <div className='lr-con'>
        <div className='lr-f-title'>Log in</div>
        <div className='l-con'>
            <form className='lr-form' onSubmit={handleSubmit}>
                <div>
                    <div className='lr-input-item'>
                        <div className={`lr-placeholder ${placeholder.uname}`}>Email</div>
                        <input className={`uname-input lr-input ${height.uname}`} onFocus={(e)=> {
                                if(!e.target.value){
                                    e.target.placeholder="" 
                                    setPlaceholder({...placeholder,"uname":"dblock"})
                                    setHeight({...height,"uname":"ishow"})
                                }
                            }
                        } 
                        onBlur={(e)=> {
                                let regex = /@/.test(credentials.email)
                                setAlert({...alert,"email":regex})
                                regex?setAlertborder({...alertborder,"email":''}):setAlertborder({...alertborder,"email":'alert-border'})
                                if(e.target.value === ''){
                                    e.target.placeholder="Enter your email address"
                                    setPlaceholder({...placeholder,"uname":"dnone"})
                                    setHeight({...height,"uname":"ihide"})
                                }
                            }
                        } 
                        placeholder='Enter your email address' onChange={onChangelogin}  value={credentials.email} type="text"  name="email" id="username"/>
                    </div>
                    {alert.email === false?<Alert error={"Enter a valid email address"}/>:<></>}
                </div>
                <div className='lr-input-item'>
                    <div className={`lr-placeholder ${placeholder.pwd}`}>Password</div>
                    <input className={`pwd-input lr-input ${height.pwd}`} onFocus={(e)=> {
                            if(!e.target.value){
                                e.target.placeholder="" 
                                setPlaceholder({...placeholder,"pwd":"dblock"})
                                setHeight({...height,"pwd":"ishow"})
                            }
                        }
                    } 
                    onBlur={(e)=> {
                            if(e.target.value === ''){
                                e.target.placeholder="Enter your password"
                                setPlaceholder({...placeholder,"pwd":"dnone"})
                                setHeight({...height,"pwd":"ihide"})
                            }
                        }
                    }   
                    placeholder='Enter your password'  onChange={onChangelogin}  value={credentials.password} type="text"  name="password"id="password"/>
                </div>
                <div className='d-flex justify-content-around'>
                    <button className='lr-btn'>Login</button>
                </div>
            </form>
            <div className='lr-link-con'>
                <Link to="/register"className='lr-link-btn'>Don’t have an account yet?</Link>
                <Link to="/register" className='lr-link-btn'>Forgot your password</Link>
            </div>
        </div>
    </div>
  )
}

export default Login
