import React,{useContext,useEffect} from 'react'
import { useNavigate } from 'react-router-dom';
import flightContent from "../context/FlightContext";

const Flightitem = (props) => {
    const {flight} = props
    console.log(flight._id)
    const content = useContext(flightContent)
    const { setSelectedflight ,setSearch,passCount, infantCounter, tripValue, settripValue, tripFlights,setTripFlights} = content 
    const host = "http://localhost:4000"
    var navigate = useNavigate()


    const bookFlight = async (flightid) => {
        if(localStorage.getItem('triptype') === 'One way'){
            const response = await fetch(`${host}/api/v1/flights?_id=${flightid}`, {
                method: 'GET',
                headers: {
                  'Content-Type': 'application/json',
                }
            });
            const json = await response.json()
            setTripFlights(tripFlights => [...tripFlights, json.flights[0]]);
            localStorage.setItem('selectedflight',JSON.stringify(tripFlights))
            navigate('/passengerdetails')
        }
        if(localStorage.getItem('triptype') === 'Return'){
            var i=0
            const response = await fetch(`${host}/api/v1/flights?_id=${flightid}`, {
                method: 'GET',
                headers: {
                  'Content-Type': 'application/json',
                }
            });
            const json = await response.json()
            updateTripFlights(i,json.flights[0])
            if(i === 0){
                navigate('/rsearch')
            }
            if(i === 1){
                console.log(i,tripFlights)
                setSearch('false')
                localStorage.setItem('tripFlights',tripFlights)
                navigate('/passengerdetails')
            }
            if(i < 1){
                console.log(i)
                i++
                console.log(i)

            }
        }
      }
      const updateTripFlights = (index, value) => {
        const utf = tripFlights.map((c, i) => {
            if (i === index) {
              // Increment the clicked counter

              return value ;
            } else {
              // The rest haven't changed
              return c;
            }
        });
        setTripFlights(utf);
      }
    useEffect(() => {
        settripValue(localStorage.getItem('triptype'))
    }, [])


  return (
    <div  className='flight-item'>
        <div className='airways-details'>
            <div className='details-item'>{flight.flightname}</div>
            <div className='details-item'>{flight.flightnumber}</div>  
        </div>
        <div className='td-details'>
            <div>
                <div className='details-item'>{flight.departuretime}</div>
                <div className='details-item'>{flight.departurecode}</div>
            </div>
            <div className='d-flex align-items-center'>
                <div className='journey-line'></div>
            </div>
            <div>
                <div className='details-item'>{flight.destinationtime}</div>
                <div className='details-item'>{flight.destinationcode}</div>
            </div>
        </div>
        <div className='d-flex align-items-center'>
            <div className='price-details'>&#8377;{(passCount-infantCounter)*flight.economyprice}</div>
        </div>
        <div className='d-flex align-items-center'>
            <button onClick={() => bookFlight(flight._id)} className='book-btn'>Select</button>
        </div>
    </div>
  )
}

export default Flightitem
