import React,{useState,useContext,useEffect} from "react";
import flightContent from "../context/FlightContext";
import { useNavigate } from "react-router-dom";
import Alert from './Alert';
import 'react-dates/initialize';
import moment from 'moment'
import { DateRangePicker,SingleDatePicker } from 'react-dates';
import 'react-dates/lib/css/_datepicker.css';
import "../styles/Home.css";


const Home = () => {

  var navigate = useNavigate()
  const [alert,setAlert] = useState({})

  // Departure-arrival
  const content = useContext(flightContent)
  const { departure, setDeparture, setArrival, arrival, filter, 
          filterDest, onChangeDeparture, onChangeArrival , departDate, 
          setdepartDate, returnDate, setreturnDate, setSearch, tripValue, 
          settripValue, adultCounter, setadultCounter, childCounter,
          setchildCounter, infantCounter, setinfantCounter, passCount,
          setPassCount, pclass, setPclass, setPassarray
        } = content 

  const [placeholder, setPlaceholder] = useState({"from":"dnone","to":"dnone"})
  const [phvalue, setPhvalue]= useState({"from":"From", "to":"To"})
  const [height,setHeight] = useState({"from":"ihide", "to":"ihide"})

  // Search for flights
  const SearchFlights = () => {
    if(departure && arrival && createdAt && pclass){
      const pa = []
      for(let i=0; i<adultCounter; i++){pa.push('Adult')}
      for(let i=0; i<childCounter; i++){pa.push('Child')}
      for(let i=0; i<infantCounter; i++){pa.push('Infant')}
      localStorage.setItem('passarray',JSON.stringify(pa))
      setPassarray(pa)
      localStorage.setItem('departure',departure)
      localStorage.setItem('destination',arrival)
      const depart = departure.split('-')
      const dest = arrival.split('-')
      console.log(depart[1],dest[1])
      localStorage.setItem('departurecode',depart[1])
      localStorage.setItem('destinationcode',dest[1])
      localStorage.setItem('triptype',tripValue)
      localStorage.setItem('passengercount',passCount)
      navigate('/owsearch')
    }
    else{
      if(departure === ''){setAlert({departure:"Departure airport is missing"})}
      if(arrival === ''){setAlert({...alert,arrival:'Destination airport is missing'})}
      if(departDate === ''){setAlert({...alert,date:'Departure date is missing'}) }
      setAlert({...alert,success:true})
    }
  }
  const handleRadioClass = (event) => {
    setPclass(event.target.value)
  } 

  useEffect(() => {
    setSearch(false)
    setDeparture(localStorage.getItem('departure'))
    setArrival(localStorage.getItem('destination'))
  }, [])

// const[flightsearch,setFlightSearch] = useState({startDate:null,endDate:null})

const [dateRange, setdateRange] = useState({
  startDate: null,
  endDate: null
});
const [focus, setFocus] = useState(null);

const { startDate, endDate } = dateRange;
const handleOnDateChange = ( startDate, endDate) =>{
  setdateRange(startDate, endDate);
}


const [calendarFocused, setCalendarFocus] = useState(false);  
const [createdAt, setCreatedAt] = useState(moment());



const onDateChange = (createdAt) => setCreatedAt(createdAt);

const onCalendarFocusChange = (focused) => setCalendarFocus(focused);
  return (
    <>
    <ul class="nav nav-tabs" id="myTab" role="tablist">
  <li class="nav-item" role="presentation">
    <button class="nav-link active" id="home-tab" data-bs-toggle="tab" data-bs-target="#home" type="button" role="tab" aria-controls="home" aria-selected="true">Book</button>
  </li>
  <li class="nav-item" role="presentation">
    <button class="nav-link" id="profile-tab" data-bs-toggle="tab" data-bs-target="#profile" type="button" role="tab" aria-controls="profile" aria-selected="false">My trips</button>
  </li>
</ul>
<div class="tab-content" id="myTabContent">
  <div class="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">
      <form className="search-flights-form">
        {alert.success?<Alert alert={alert}/>:<></>}
        <div className="f-q-container  d-flex">
          {/* to and from */}
          <div className="desti-con d-flex">
            {/* <div className="dropdown da-select departure-select">
                <div className='dropdown-toggle da-dropdown-btn departure-drop-btn' data-bs-toggle="dropdown" aria-expanded="false">
                    <div className={`da-placeholder ${placeholder.from}`}>From</div>
                    <div>
                        <input className={`departure-input da-input ${height.from}`} 
                          onFocus={(e)=> {
                              if(!e.target.value){
                                  e.target.placeholder="" 
                                  setPlaceholder({...placeholder,"from":"dblock"})
                                  setHeight({...height,"from":"ishow"})
                              }
                            }
                          } 
                          onBlur={(e)=> {
                                  if(e.target.value === ''){
                                      e.target.placeholder="From" 
                                      setPlaceholder({...placeholder,"from":"dnone"})
                                      setHeight({...height,"from":"ihide"})
                                  }
                              }
