import React, { useState } from 'react'
import { useNavigate, } from 'react-router-dom'
import "../styles/Register.css";
import Alert from './Alert';

const Register = () => {
    let navigate = useNavigate();
    const [credentials, setCredentials] = useState({ "email": "", "password": "", "cpassword": "", "countrycode": "+91", "mobilenumber": "", "firstname": "", "lastname": "", "dateofbirth": "", "gender": "", "state": "Select State" })
    const [placeholder, setPlaceholder] = useState({ "fname": "dnone", "lname": "dnone", "email": "dnone", "pwd": "dnone", "cpwd": "dnone", "countrycode": "dblock", "mobilenumber": "dnone", "dob": "dblock" })
    const [height, setHeight] = useState({ "fname": "ihide", "lname": "ihide", "email": "ihide", "pwd": "ihide", "cpwd": "ihide", "countrycode": "ishow", "mobilenumber": "ihide", "dob": "ishow" })
    const [alert, setAlert] = useState({})
    const [alertborder, setAlertborder] = useState({ "email": "", "password": "", "cpassword": "", "countrycode": "", "mobilenumber": "", "firstname": "", "lastname": "", "dateofbirth": "", "gender": "", "state": "" })
    const onChangelogin = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value })
    }
    const handleRadio = (e) => {
        setCredentials({ ...credentials, 'gender': e.target.value })
    }
    const [invalid, setInvalid] = useState({})

    const handleSubmit = async (e) => {
        console.log(credentials)
        e.preventDefault();
        const response = await fetch("http://localhost:4000/api/v1/auth", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: credentials.email,
                password: credentials.password,
                countrycode: credentials.countrycode,
                mobilenumber: credentials.mobilenumber,
                firstname: credentials.firstname,
                lastname: credentials.lastname,
                dateofbirth: credentials.dateofbirth,
                gender: credentials.gender,
                state: credentials.state
            })
        })
        const json = await response.json()
        console.log(json)
        if (json.success) {
            localStorage.setItem('token', json.authtoken);
            const data = await fetch("http://localhost:4000/api/v1/auth/createpassenger", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'auth-token': localStorage.getItem('token')
                },
                body: JSON.stringify({
                    firstname: credentials.firstname,
                    lastname: credentials.lastname,
                    dateofbirth: credentials.dateofbirth,
                    gender: credentials.gender,
                    state: credentials.state
                })
            })
            const passenger = await data.json()
            console.log(passenger)
            navigate('/');
        }
        else {
            window.alert('try again')
        }
    }
    const selectState = (e) => {
        setCredentials({ ...credentials, "state": e.target.getAttribute('data-value') })
    }
    //   const validationCheck = () =>{
    //     if(alert.email && alert.password && alert.cpassword && alert.mobilenumber && alert.firstname && alert.lastname && alert.dateofbirth){
    //         return false
    //     }
    //     else{
    //         return true
    //     }
    //   }


    return (
        <div className='register-con'>
            <div className='lr-f-title'>Register</div>
            <form className='lr-form' onSubmit={handleSubmit}>
                <div className='cred-con part-con'>
                    <div>Let's create your credentials</div>
                    <div>
                        <div>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-input form-control ${invalid.email}`}
                                        placeholder='Enter your email address'
                                        onChange={onChangelogin}
                                        value={credentials.password}
                                        type="text"
                                        name="email"
                                        id="email"
                                        onBlur={() => {
                                            let regex = /@/.test(credentials.email)
                                            setAlert({ ...alert, "email": regex })
                                            setInvalid({ ...invalid, 'email': regex ? '' : 'is-invalid' })
                                        }
                                        }
                                    />
                                    <label className='cd-innput-label' htmlFor="email">Enter your email address</label>
                                </div>
                                {alert.email === false ? <Alert error={"Enter a valid email address"} /> : <></>}
                            </div>
                            {/* {alert.email === false ? <Alert error={"Enter a valid email address"} /> : <></>} */}
                        </div>

                        <div className='d-flex'>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-mutli-input-mr form-control ${invalid.password}`}
                                        placeholder='Enter your password'
                                        onChange={onChangelogin}
                                        value={credentials.password}
                                        type="text"
                                        name="password"
                                        id="password"
                                        onBlur={() => {
                                            let regex = /^[a-zA-Z]{1,}$/.test(credentials.password)
                                            setAlert({ ...alert, "password": regex })
                                            setInvalid({ ...invalid, 'password': regex ? '' : 'is-invalid' })
                                        }
                                        }
                                    />
                                    <label className='cd-innput-label' htmlFor="password">Enter your password</label>
                                </div>
                                {alert.password === false ? <Alert error={"Please enter a valid password"} /> : <></>}
                            </div>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-mutli-input form-control ${invalid.cpassword}`}
                                        placeholder='Confirm your password'
                                        onChange={onChangelogin}
                                        value={credentials.cpassword}
                                        type="text"
                                        name="cpassword"
                                        id="cpassword"
                                        onBlur={() => {
                                            if(credentials.password === credentials.cpassword){
                                                setAlert({ ...alert, "cpassword": true })
                                                setInvalid({ ...invalid, 'cpassword': true ? '' : 'is-invalid' })
                                            }
                                            if(credentials.password !== credentials.cpassword){
                                                setAlert({ ...alert, "cpassword": false })
                                                setInvalid({ ...invalid, 'cpassword': false ? '' : 'is-invalid' })
                                            }
                                        }
                                        }
                                    />
                                    <label className='cd-innput-label' htmlFor="password">Confirm your password</label>
                                </div>
                                {alert.cpassword === false ? <Alert error={"Password does not match"} /> : <></>}
                            </div>
                        </div>
                        <div className='d-flex'>
                            <div className='lr-input-item'>
                                <div className={`lr-placeholder ${placeholder.countrycode}`}>Country code</div>
                                <div className={`mutli-input lr-input ${height.countrycode}`}>
                                    {credentials.countrycode}
                                </div>
                            </div>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-mutli-input form-control ${invalid.mobilenumber}`} 
                                    type="tel" 
                                    maxLength={10} 
                                    onChange={onChangelogin} 
                                    value={credentials.mobilenumber} 
                                    name="mobilenumber" 
                                    id="mobilenumber" 
                                    placeholder="Mobile number"
                                        onBlur={() => {
                                            let regex = /^[0-9]{10,}$/.test(credentials.mobilenumber)
                                            setAlert({ ...alert, "mobilenumber": regex })
                                            setInvalid({ ...invalid, 'mobilenumber': regex ? '' : 'is-invalid' })
                                        }
                                        }
                                        disabled={localStorage.getItem('token')}
                                    />
                                    <label htmlFor="mobilenumber">Mobile number</label>
                                </div>
                                {/* {alert.mobilenumber === false ? <Alert error={"Your mobile number should be 10 characters long"} /> : <></>} */}
                            </div>
                        </div>
                        {alert.mobilenumber === false ? <Alert error={"Your mobile number should be 10 characters long"} /> : <></>}
                    </div>
                </div>
                <div className='pd-con part-con'>
                    <div>Your personal details</div>
                    <div>
                        <div className='d-flex'>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-mutli-input-mr form-control ${invalid.firstname}`}
                                        placeholder='First name'
                                        onChange={onChangelogin}
                                        value={credentials.firstname}
                                        type="text"
                                        name="firstname"
                                        id="firstname"
                                        onBlur={() => {
                                            let regex = /^[a-zA-Z]{1,}$/.test(credentials.firstname)
                                            setAlert({ ...alert, "firstname": regex })
                                            setInvalid({ ...invalid, 'firstname': regex ? '' : 'is-invalid' })
                                        }
                                        }
                                    />
                                    <label className='cd-innput-label' htmlFor="firstname">First name</label>
                                </div>
                                {alert.firstname === false ? <Alert error={"Please enter a first name"} /> : <></>}
                            </div>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-mutli-input form-control ${invalid.lastname}`}
                                        placeholder='Last name' 
                                        onChange={onChangelogin} 
                                        value={credentials.lastname} 
                                        type="text" name="lastname" 
                                        id="lastname"
                                        onBlur={() => {
                                            let regex = /^[a-zA-Z]{1,}$/.test(credentials.lastname)
                                            setAlert({ ...alert, "lastname": regex })
                                            setInvalid({ ...invalid, 'lastname': regex ? '' : 'is-invalid' })
                                        }
                                        }
                                    />
                                    <label className='cd-innput-label' htmlFor="lastname">Last name</label>
                                </div>
                                {alert.lastname === false ? <Alert error={"Please enter a last name"} /> : <></>}
                            </div>
                        </div>
                        <div className='d-flex'>
                            <div>
                                <div className="form-floating">
                                    <input className={`lr-mutli-input-mr form-control ${invalid.dateofbirth}`}
                                       placeholder='Date of birth' 
                                       onChange={onChangelogin} 
                                       value={credentials.dateofbirth} 
                                       type="date" 
                                       name="dateofbirth" 
                                       id="dateofbirth"
                                        onBlur={() => {
                                            let regex = /\d{1,}/g.test(credentials.dateofbirth)
                                            setAlert({ ...alert, "dateofbirth": regex })
                                            setInvalid({ ...invalid, 'dateofbirth': regex ? '' : 'is-invalid' })
                                        }
                                        }
                                    />
                                    <label className='cd-innput-label' htmlFor="dateofbirth">Date of birth</label>
                                </div>
                                {alert.dateofbirth === false ? <Alert error={"Please enter a date of birth"} /> : <></>}
                            </div>
                            <div>
                                <div>Gender (Optional)</div>
                                <div className='select-radio-con'>
                                    <div className={`form-check select-radio`}>
                                        <input className={`form-check-input select-input  ${invalid.gender} `}
                                            onChange={(e) =>{
                                                handleRadio(e)
                                            }
                                            }
                                            checked={credentials.gender === 'Male'}
                                            value="Male" 
                                            type="radio"
                                            name="genderOptions"
                                            id="male"
                                            onBlur={()=>{
                                                let regex = /^[a-zA-Z]{1,}$/.test(credentials.gender)
                                                setAlert({ ...alert, "gender": regex })
                                                setInvalid({ ...invalid,'gender':regex?'':'is-invalid'})
                                            }}
                                        />
                                        <label className="form-check-label select-label" htmlFor="male">
                                            Male
                                        </label>
                                    </div>
                                    <div className={`form-check select-radio `}>
                                        <input className={`form-check-input select-input  ${invalid.gender}`}
                                            onChange={handleRadio} 
                                            type="radio"
                                            checked={credentials.gender === 'Female'}
                                            value="Female"
                                            name="genderOptions"
                                            id="female" 
                                            onBlur={()=>{
                                                let regex = /^[a-zA-Z]{1,}$/.test(credentials.gender)
                                                setAlert({ ...alert, "gender": regex })
                                                setInvalid({ ...invalid,'gender':regex?'':'is-invalid'})
                                            }}
                                        />
                                        <label className="form-check-label select-label" htmlFor="female">
                                            Female
                                        </label>
                                    </div>
                                </div>
                                {alert.gender === false ? <Alert error={"Please select a gender"} /> : <></>}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='wliv-con part-con'>
                    <div>Where do you live ?</div>
                    <div>
                        <div className="dropdown">
                            <div className={`dropdown-toggle lr-dropdown lr-input-item live-dd ${alertborder.state}`} role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                {credentials.state}
                            </div>
                            <div className="dropdown-menu dd-menu">
                                <div onClick={selectState} data-value="Andhra Pradesh" className="dropdown-item">Andhra Pradesh</div>
                                <div onClick={selectState} data-value="Arunachal Pradesh" className="dropdown-item">Arunachal Pradesh</div>
                                <div onClick={selectState} data-value="Assam" className="dropdown-item">Assam</div>
                                <div onClick={selectState} data-value="Bihar" className="dropdown-item">Bihar</div>
                                <div onClick={selectState} data-value="Chhattisgarh" className="dropdown-item">Chhattisgarh</div>
                                <div onClick={selectState} data-value="Goa" className="dropdown-item">Goa</div>
                                <div onClick={selectState} data-value="Gujarat" className="dropdown-item">Gujarat</div>
                                <div onClick={selectState} data-value="Haryana" className="dropdown-item">Haryana</div>
                                <div onClick={selectState} data-value="Himachal Pradesh" className="dropdown-item">Himachal Pradesh</div>
                                <div onClick={selectState} data-value="Jharkhand" className="dropdown-item">Jharkhand</div>
                                <div onClick={selectState} data-value="Karnataka" className="dropdown-item">Karnataka</div>
                                <div onClick={selectState} data-value="Kerala" className="dropdown-item">Kerala</div>
                                <div onClick={selectState} data-value="Madhya Pradesh" className="dropdown-item">Madhya Pradesh</div>
                                <div onClick={selectState} data-value="Maharashtra" className="dropdown-item">Maharashtra</div>
                                <div onClick={selectState} data-value="Manipur" className="dropdown-item">Manipur</div>
                                <div onClick={selectState} data-value="Meghalaya" className="dropdown-item">Meghalaya</div>
                                <div onClick={selectState} data-value="Mizoram" className="dropdown-item">Mizoram</div>
                                <div onClick={selectState} data-value="Nagaland" className="dropdown-item">Nagaland</div>
                                <div onClick={selectState} data-value="Odisha" className="dropdown-item">Odisha</div>
                                <div onClick={selectState} data-value="Punjab" className="dropdown-item">Punjab</div>
                                <div onClick={selectState} data-value="Rajasthan" className="dropdown-item">Rajasthan</div>
                                <div onClick={selectState} data-value="Sikkim" className="dropdown-item">Sikkim</div>
                                <div onClick={selectState} data-value="Tamil Nadu" className="dropdown-item">Tamil Nadu</div>
                                <div onClick={selectState} data-value="Telangana" className="dropdown-item">Telangana</div>
                                <div onClick={selectState} data-value="Tripura" className="dropdown-item">Tripura</div>
                                <div onClick={selectState} data-value="Uttarakhand" className="dropdown-item">Uttarakhand</div>
                                <div onClick={selectState} data-value="Uttar Pradesh" className="dropdown-item">Uttar Pradesh</div>
                                <div onClick={selectState} data-value="West Bengal" className="dropdown-item">West Bengal</div>
                            </div>
                        </div>
                        {/* {credentials.state === 'Select State'?<Alert error={"Please select your state you live in"}/>:<></>} */}
                    </div>
                </div>
                <div className='btn-con'>
                    <button className='lr-btn' disabled={alert.email && alert.password && alert.cpassword && alert.mobilenumber && alert.firstname && alert.lastname && alert.dateofbirth ? false : true}>Create an account</button>
                </div>
            </form>
        </div>
    )
}

export default Register
