}


const [calendarFocused, setCalendarFocus] = useState(false);  

const [createdAt, setCreatedAt] = useState('');


const onDateChange = (element) => {
  updateSearchFlights(3,element)
}


const updateSearchFlights = (index, value) => {
  const utf = searchflights.map((c, i) => {
      if (i === index) {
        return value ;
      } else {
        return c;
      }
  });
  setSearchFlights(utf);
}




const onCalendarFocusChange = (focused) => setCalendarFocus(focused);

