import React,{useState} from 'react'
import flightContent from './FlightContext'

const FlightState = (props) => {
    const host = "http://localhost:4000"
    const flightsInitial = []
    const [flights, setflights] = useState(flightsInitial)

    const getFlights = async() => {
        const response = await fetch(`${host}/api/v1/flights`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            }
        });
        const json = await response.json() 
        setFlights(json)
    }

  return (
    <flightContent.Provider value={{flights, getFlights}}>
        {props.children}
    </flightContent.Provider>
  )
}

export default FlightState
