import React,{useState} from 'react'
import flightContent from './FlightContext'

const FlightState = (props) => {
    const host = "http://localhost:4000"
    const flightsInitial = []
    const [flights, setflights] = useState(flightsInitial)

    // From-to-component

    const Airports = [
      {
          name:"Netaji Subhash Chandra Bose International Airport",
          city:"Kolkata",
          id:"CCA",
          country:"India"
      },
      {
          name:"Chennai International Airport",
          city:"Chennai",
          id:"CHE",
          country:"India"
      },
      {
          name:"Thiruvananthapuram International Airport",
          city:"Thiruvananthapuram",
          id:"CCC",
          country:"India"
      },
      {
          name:"Sardar Vallabh Bhai Patel International Airport",
          city:"Ahmedabad",
          id:"CCD",
          country:"India"
      },
      {
          name:"Guru Ram Dass Jee International Airport",
          city:"Amritsar",
          id:"CCE",
          country:"India"
      },
      {
          name:"Lokpriya Gopinath Bordoloi International Airport",
          city:"Guwahati",
          id:"CCF",
          country:"India"
      },
      {
          name:"Goa International Airport",
          city:"Goa",
          id:"CCJ",
          country:"India"
      },
      {
          name:"Srinagar International Airport",
          city:"Srinagar",
          id:"CCH",
          country:"India"
      }, 
      {
          name:"Jaipur International Airport",
          city:"Jaipur",
          id:"CCI",
          country:"India"
      },
      {
          name:"Kozhikode Airport",
          city:"Calicut",
          id:"CCZ",
          country:"India"
      },
      {
          name:"Veer Savarkar International Airport",
          city:" Port Blair, A&N Islands",
          id:"CCK",
          country:"India"
      },
      {
          name:"Indira Gandhi International Airport",
          city:"Delhi",
          id:"DEL",
          country:"India"
      },
      {
          name:"Chattrapati Shivaji International Airport",
          city:"Mumbai",
          id:"CCM",
          country:"India"
      },
      {
          name:"GMR Hyderabad International Airport",
          city:"Hyderabad",
          id:"HYD",
          country:"India"
      },
      {
          name:"Bangalore International Airport Limited",
          city:"Bengaluru",
          id:"BEN",
          country:"India"
      },
      {
          name:"Cochin International Airport",
          city:"Kochi",
          id:"CCP",
          country:"India"
      },
      {
          name:"Bharat Ratna Babasaheb Dr. B.R. Ambedkar International Airport",
          city:"Nagpur",
          id:"CCQ",
          country:"India"
      },

  ]
  const [departure, setDeparture] = useState('')
  const [arrival, setArrival] = useState('')
  const [filter, setFilter] = useState(Airports)

  const onChangeArrival = (e) => {
      setArrival(e.target.value)
      filterDest(e.target.value)
  }
  const onChangeDeparture = (e) => {
      setDeparture(e.target.value)
      filterDest(e.target.value)
  }
  const filterDest = (value) =>{
      const result = Airports.filter((element)=> {
          const {name,city} = element
          var cityfilter = city.toLowerCase()
          var namefilter = name.toLowerCase()
          if(cityfilter.indexOf(value.toLowerCase()) > -1 || namefilter.indexOf(value.toLowerCase()) > -1){
              return element
          }
      })
    setFilter(result)
  }


  //
  const [tripValue, settripValue] = useState('One way')
  const [departDate,setdepartDate]=useState('')
  const [search,setSearch] = useState('')



    const getFlights = async(from,to,departuredate) => {
        const response = await fetch(`${host}/api/v1/flights?from=${from}&to=${to}&departuredate=${departuredate}`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            }
        });
        const json = await response.json()
        setflights(json.flights)
    } 

  // Flightitem
    const[ selectedflight, setSelectedflight]= useState({})






  return (
    <flightContent.Provider value={{departure, setDeparture, arrival, setArrival ,filter, Airports, 
    flights, filterDest, onChangeDeparture, onChangeArrival, getFlights,
    tripValue, settripValue, departDate, setdepartDate, search, setSearch,
    selectedflight, setSelectedflight,}}>
        {props.children}
    </flightContent.Provider>
  )
}

export default FlightState
