import React,{useContext, useState,useEffect} from 'react'
import '../styles/Passengerdetails.css'
import flightContent from "../context/FlightContext";
import Alert from './Alert';
import { useNavigate } from 'react-router-dom';
const Passengerdetails = () => {
    const [placeholder, setPlaceholder] = useState({"fname":"dnone", "lname":"dnone","dob":"dblock","nationality":"dnone","passport":"dnone","passportexpirydate":"dblock","mobilenumber":"dnone","email":"dnone" })
    const [height,setHeight] = useState({"fname":"ihide", "lname":"ihide","dob":"ishow","nationality":"ihide","passport":"ihide","passportexpirydate":"ishow","mobilenumber":"ihide","email":"ihide"})
    const [invalid,setInvalid] = useState({"mobilenumber":"","email":""})

    let navigate = useNavigate()
    const content = useContext(flightContent)
    const {tripValue, settripValue, personaldetails, setPersonaldetials, contactdetails, setContactdetails, tripFlights, setTripFlights ,passarray,setPassarray, adultCounter, childCounter,infantCounter ,passCount,localpassengers, setLocalPassengers,localpassenger, setLocalPassenger,getUsersdata} = content 

    const onChangelogin = (e) => {
        setPersonaldetials({ ...personaldetails,[e.target.name]: e.target.value})
       }
       const onChangepd = (e) => {
        setPersonaldetials({ ...personaldetails,[e.target.name]: e.target.value})
       }
     const onChangecd = (e) => {
         setContactdetails({ ...contactdetails,[e.target.name]: e.target.value})
     }
    // Retriving passenger details 

    const [passengers,setPassengers]= useState([])
    useEffect(() => {
        console.log(tripFlights)
        setPassarray(JSON.parse(localStorage.getItem('passarray')))
        settripValue(localStorage.getItem('triptype'))
        // setTripFlights(JSON.parse(localStorage.getItem('tripFlights')))
    }, [])

    // Passenger drop-down
    const [pselected, setPselected] = useState("Select passenger");
    const onCP = (e) => {
        setPselected(e.target.getAttribute('data-value'))
        passengers.forEach((element)=>{
            if(element.firstname+element.lastname === e.target.getAttribute('data-value')){
               setPersonaldetials({ ...personaldetails,"firstname":element.firstname,
                                                "lastname":element.lastname,})
            }
        })
    }

    // Passenger validation alerts

     const [alert,setAlert] = useState({})
     const [alertborder, setAlertborder] = useState({"firstname":"", "lastname":"","dateofbirth":"","gender":"","state":""})



    //  Passengers validation and accordion for mutliple passenger

    const [passvalidation,setPassvalidation] = useState(false)
    const [erroraccord, setErroraccord] = useState({})
    const [adddetails, setAdddetails] = useState([false,true])
    const [passengernum,setPassengernum] = useState(1)

    const addDeatilsClick = (e) => {
        console.log(e.target.getAttribute('data-value'))
        if(!passvalidation){
            setErroraccord({...erroraccord,[e.target.getAttribute('data-value')]:true})
            pdvalidationcheck()
        }
        if(passvalidation){
            console.log('add',adddetails)
            setErroraccord({...erroraccord,[e.target.getAttribute('data-value')]:false})
            setAdddetails({...adddetails,[e.target.getAttribute('data-value')]:true})
            localpassengers[passengernum]= localpassenger
            setPassengernum((passengernum) => Number(e.target.getAttribute('data-value')))
            setPassvalidation(false)
            setPersonaldetials(
                    {...personaldetails,
                            firstname:'',
                            lastname:'',
                            dateofbirth:'',
                            nationality:'',
                    }
                )

        }
    }

    const pdvalidationcheck = () => {
        let efname =/^[a-zA-Z]{1,}$/.test(personaldetails.firstname)
        let elname =/^[a-zA-Z]{1,}$/.test(personaldetails.lastname)
        let enationality =/^[a-zA-Z]{1,}$/.test(personaldetails.nationality)
        let edob =/^[]{1,}$/.test(personaldetails.dateofbirth)
        setAlert({...alert,"firstname":efname,"lastname":elname,"nationality":enationality,"dateofbirth":edob})
        setAlertborder({...alertborder,"firstname":efname?'':'alert-border',"lastname":elname?'':'alert-border',"nationality":enationality?'':'alert-border',"dateofbirth":edob?'':'alert-border'})
    } 

    const editDetailsClick = (e) => {
        console.log(e.target.getAttribute('data-value'))
        if(!passvalidation){
            setErroraccord({...erroraccord,[e.target.getAttribute('data-value')]:true})
            pdvalidationcheck()
        }
        if(passvalidation){
            console.log('edit',adddetails)
            let value= e.target.getAttribute('data-value')
            setErroraccord({...erroraccord,[value]:false}) 
            setAdddetails({...adddetails,[value]:true})
            localpassengers[passengernum]= localpassenger
            setPassengernum((passengernum) => Number(e.target.getAttribute('data-value')))
            setPassvalidation(false)
            setPersonaldetials(
                {...personaldetails,
                        firstname:localpassengers[value].firstname,
                        lastname:localpassengers[value].lastname
                }
            )
            localpassengers[value]=''
        }
    }

    useEffect(() => {
        if(personaldetails.firstname !== '' && personaldetails.lastname !== '' && personaldetails.dateofbirth !== '' && personaldetails.nationality !== ''){
            setPassvalidation(true)
        }
        else{
            setPassvalidation(false)
        }
        setLocalPassenger({...localpassenger, passengernumber:passengernum,
            firstname:personaldetails.firstname,
            lastname:personaldetails.lastname,
            dateofbirth:personaldetails.dateofbirth,
            nationality:personaldetails.nationality,
            passport:personaldetails.passport,
            expirydate:personaldetails.expirydate,
            type:passarray[passengernum-1]
        })
        console.log(localpassenger,localpassengers)
    }, [personaldetails.firstname,personaldetails.lastname,personaldetails.dateofbirth,personaldetails.nationality,personaldetails.passport,personaldetails.expirydate])

    useEffect(()=>{
        if(localStorage.getItem('token')){
            const controller = new AbortController();
            const signal = controller.signal;
            getUsersdata(signal)
            .then((element)=>{
                setContactdetails({...contactdetails,'mobilenumber':element.user.mobilenumber,'email':element.user.email})
            })
            .catch(err=>{
                if(err.name === "AbortError"){
                console.log('aborted')
                }
            })
            return () => {
                controller.abort();
            }
        } 
    },[])

    // Proceed Method

    const proceed = () => {
        pdvalidationcheck()
        if(passvalidation){
            localpassengers[passengernum]= localpassenger
            console.log(localpassengers.length,passarray.length,localpassengers)
            setPersonaldetials(
                {...personaldetails,
                        firstname:'',
                        lastname:'',
                        dateofbirth:'',
                        nationality:'',
                }
            )
            if((localpassengers.length-1) === passarray.length && contactdetails.mobilenumber && contactdetails.email){
                console.log('navigate to booking summary')
                localpassengers.shift()
                localStorage.setItem('passengersinfo',JSON.stringify(localpassengers))
                localStorage.setItem('contactinfo',JSON.stringify(contactdetails))

                console.log(JSON.stringify(localpassengers))
                navigate('/bookingsummary')
            }
            setPassvalidation(false)
        }
    } 



  return (
    <div className='passenger-d-con'>
        <div className='pd-title'>Passenger Details</div>
        <div className='d-flex justify-content-between'>
            <div>
                <div className="accordion open" id="passengerAccordion">
                    {passarray.map((element,index)=>{
                    return  <div className="accordion-item pass-accord-item">
                                <div className="pass-accord-header"  id={`heading${index=index+1}`}  >
                                    <div className='d-flex justify-content-between align-items-center'>
                                    <div className='pd-form-title'>{localpassengers[index]?localpassengers[index].firstname+localpassengers[index].lastname:<>Passenger {index}</>} ({element})</div>
                                        {!adddetails[index]?<div className="cord-add-d-btn" onClick={addDeatilsClick} data-value={index} data-bs-toggle="collapse" data-bs-target={passvalidation?`#collapse${index}`:''} aria-expanded='true' aria-controls={`collapse${index}`}>Add details</div>:<></>}
                                        {localpassengers[index]?<div className="cord-add-d-btn" onClick={editDetailsClick} data-value={index} data-bs-toggle="collapse" data-bs-target={passvalidation?`#collapse${index}`:''} aria-expanded='true' aria-controls={`collapse${index}`}>Edit Details</div>:<></>}
                                    </div>
                                </div>
                                <div className='d-flex justify-content-around'>
                                    {erroraccord[index]?<div className='next-step-alert'>Please complete the details of the remaining passengers in your booking to continue to the next step.</div>:<></>}
                                </div>
                                <div id={`collapse${index}`} className={`accordion-collapse collapse ${index===1?'show':''}`} aria-labelledby={`heading${index}`} data-bs-parent="#passengerAccordion">
                                    <div className="accordion-body pass-accord-body">
                                        <form  className='pd-form'>
                                            <div>{localStorage.getItem('token')?
                                                    <div className="dropdown">
                                                        <div className="btn btn-secondary dropdown-toggle" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                                            {pselected}
                                                        </div>
                                                        <div className="dropdown-menu">
                                                        {passengers.map((element)=>{
                                                        return <div className="dropdown-item" onClick={onCP} data-value={element.firstname+element.lastname}>{element.firstname+element.lastname}</div>
                                                        })}
                                                    </div>
                                                </div>:<></>}
                                            </div>
                                            <div className='d-flex'>
                                                <div>
