import React,{useContext, useState,useEffect} from 'react'
import '../styles/Passengerdetails.css'
import flightContent from "../context/FlightContext";

const Passengerdetails = () => {
    const [credentials, setCredentials] = useState({title:"",firstname:"",lastname:"",dateofbirth:"",nationality:"",gender:"",passport:"",passportexpirydate:""})
    const [placeholder, setPlaceholder] = useState({"fname":"dnone", "lname":"dnone","dob":"dblock","nationality":"dnone","passport":"dnone","passportexpirydate":"dblock" })
    const [height,setHeight] = useState({"fname":"ihide", "lname":"ihide","dob":"ishow","nationality":"ihide","passport":"ihide","passportexpirydate":"ishow" })
    const [passengers,setPassengers]= useState([])
    const onChangelogin = (e) => {
        setCredentials({ ...credentials,[e.target.name]: e.target.value})
      }

    const content = useContext(flightContent)
    const {selectedflight ,passarray, user, getUsersdata} = content 


    // retriving passenger details 
    useEffect(() => {
        if(localStorage.getItem('token')){
            getUsersdata()
            getPassengers()
        }
        // eslint-disable-next-line
    }, [])
    const getPassengers = async () => {
        const data = await fetch("http://localhost:4000/api/v1/auth/getpassengers",{
            method:'GET',
            headers: {
            'Content-Type': 'application/json',
            'auth-token': localStorage.getItem('token')
            },
        })
        const passenger = await data.json()
        setPassengers(passenger.passenger)
        console.log(passenger)
    }

    // passenger drop-down
    const [pselected, setPselected] = useState("Select passenger");
    const onCP = (e) => {
        setPselected(e.target.getAttribute('data-value'))
        console.log('hi')
        setCredentials({ ...credentials,"firstname":passengers.firstname})
    }


  return (
    <div className='passenger-d-con'>
        <div className='pd-title'>Passenger Details</div>
        <div className='d-flex'>
            <div>{passarray.map((element,index)=> {
                return                 <form  className='pd-form'>
                <div className='pd-form-title'>Passenger {index=index+1} ({element})</div>
                    <div>{localStorage.getItem('token')?
                            <div class="dropdown">
                                <div class="btn btn-secondary dropdown-toggle" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                    {pselected}
                                </div>
                                <div class="dropdown-menu">
                                {passengers.map((element)=>{
                                return <div class="dropdown-item" onClick={onCP} data-value={element.firstname+element.lastname}>{element.firstname+element.lastname}</div>
                                })}
                            </div>
                        </div>:<></>}
                </div>
                <div className='radio-heading'>Title</div>
                <div className='select-radio-con'>
                    <div className="form-check select-radio">
                        <input className="form-check-input select-input" type="radio" name="titleOptions" id="titleOptions1"/>
                        <label className="form-check-label select-label" htmlFor="titleOptions1">
                            Mr
                        </label>
                    </div>
                    <div className="form-check select-radio">
                        <input className="form-check-input select-input" type="radio" name="titleOptions" id="titleOptions2" />
                        <label className="form-check-label select-label" htmlFor="titleOptions2">
                            Mrs
                        </label>
                    </div>
                </div>
                <div className='d-flex'>
                    <div className='pd-input-item'>
                        <div className={`pd-placeholder ${placeholder.fname}`}>First name</div>
                        <input className={`fname-input pd-input ${height.fname}`} onFocus={(e)=> {
                                if(!e.target.value){
                                    e.target.placeholder="" 
                                    setPlaceholder({...placeholder,"fname":"dblock"})
                                    setHeight({...height,"fname":"ishow"})
                                }
                            }
                        } 
                        onBlur={(e)=> {
                                if(e.target.value === ''){
                                    e.target.placeholder="First name"
                                    setPlaceholder({...placeholder,"fname":"dnone"})
                                    setHeight({...height,"fname":"ihide"})
                                }
                            }
                        } 
                        placeholder='First name' onChange={onChangelogin}  value={credentials.firstname} type="text"  name="firstname" id="firstname"/>
                    </div>
                    <div className='pd-input-item'>
                        <div className={`pd-placeholder ${placeholder.lname}`}>Last name</div>
                        <input className={`lname-input pd-input ${height.lname}`} onFocus={(e)=> {
                                if(!e.target.value){
                                    e.target.placeholder="" 
                                    setPlaceholder({...placeholder,"lname":"dblock"})
