                            }
                        } 
                        placeholder='Last name' onChange={onChangelogin}  value={credentials.lastname} type="text"  name="lastname" id="lastname"/>
                    </div>
                </div>
                <div className='d-flex'>
                    <div className='pd-input-item'>
                        <div className={`pd-placeholder ${placeholder.dob}`}>Date of birth</div>
                        <input className={`dob-input pd-input ${height.dob}`} 
                        placeholder='Date of birth' onChange={onChangelogin}  value={credentials.dateofbirth} type="date"  name="dateofbirth" id="dateofbirth"/>
                    </div>
                    <div className='pd-input-item'>
                        <div className={`pd-placeholder ${placeholder.nationality}`}>Nationality</div>
                        <input className={`dob-input pd-input ${height.nationality}`} onFocus={(e)=> {
                                if(!e.target.value){
                                    e.target.placeholder="" 
                                    setPlaceholder({...placeholder,"nationality":"dblock"})
                                    setHeight({...height,"nationality":"ishow"})
                                }
                            }
                        } 
                        onBlur={(e)=> {
                                if(e.target.value === ''){
                                    e.target.placeholder="Nationality"
                                    setPlaceholder({...placeholder,"nationality":"dnone"})
                                    setHeight({...height,"nationality":"ihide"})
                                }
                            }
                        } 
                        placeholder='Nationality' onChange={onChangelogin}  value={credentials.nationality} type="text"  name="dateofbirth" id="dateofbirth"/>
                    </div>
                </div>
                <div className='select-radio-con'>
                    <div className="form-check select-radio">
                        <input className="form-check-input select-input" type="radio" name="genderOptions" id="genderOptions1"/>
                        <label className="form-check-label select-label" htmlFor="genderOptions1">
                            Male
                        </label>
                    </div>
                    <div className="form-check select-radio">
                        <input className="form-check-input select-input" type="radio" name="genderOptions" id="genderOptions2" checked/>
                        <label className="form-check-label select-label" htmlFor="genderOptions2">
                            Female
                        </label>
                    </div>
                </div>
                <div className='d-flex'>
                    <div className='pd-input-item'>
                        <div className={`pd-placeholder ${placeholder.passport}`}>Passport Details(Optional)</div>
                        <input className={`passport-input pd-input ${height.passport}`} onFocus={(e)=> {
                                if(!e.target.value){
                                    e.target.placeholder="" 
                                    setPlaceholder({...placeholder,"passport":"dblock"})
                                    setHeight({...height,"passport":"ishow"})
                                }
                            }
                        } 
                        onBlur={(e)=> {
                                if(e.target.value === ''){
                                    e.target.placeholder="Passport Details(Optional)"
                                    setPlaceholder({...placeholder,"passport":"dnone"})
                                    setHeight({...height,"passport":"ihide"})
                                }
                            }
                        } 
                        placeholder='Passport Details(Optional)' onChange={onChangelogin}  value={credentials.passport} type="text"  name="passport" id="passport"/>
                    </div>
                    <div className='pd-input-item'>
                        <div className={`pd-placeholder ${placeholder.passportexpirydate}`}>Passport expirydate</div>
                        <input className={`passportexpirydate-input pd-input ${height.passportexpirydate}`}  
                        placeholder='Passport expirydate' onChange={onChangelogin}  value={credentials.expirydate} type="date"  name="passportexpirydate" id="passportexpirydate"/>
                    </div>
                </div>
                </form>
            })}
            </div>
            <div className='trip-summary-con'>
                <div className='ts-title'>Your trip summary</div>
                {selectedflight.map((flight)=>{
                        return                         <div className='fd-con'>
                        <div className='ts-body'>
                            <div className='ts-item'>Outbound flight</div>
                            <div className='d-flex justify-content-between'>
                                <div className='ts-item'>{flight.departuredate}</div>
                                <div className='ts-item'>{flight.arrivaldate}</div>
                            </div>
                            <div className='d-flex'>
                                <div>
                                    <div className='ts-item'>{flight.from}</div>
                                    <div className='ts-item'>{flight.departuretime}</div>
