import React,{useContext,useEffect} from 'react'
import Flightitem from "./Flightitem";
import flightContent from "../context/FlightContext";


const Searchflights = () => {
    const content = useContext(flightContent)
    const {flights, getFlights, setFlights, setSearch, departure, setDeparture, arrival, setArrival} = content 

    useEffect(() => {
      setSearch('true')
      setDeparture(localStorage.getItem('departure'))
      setArrival(localStorage.getItem('destination'))
      const controller = new AbortController();
      const signal = controller.signal;
      getFlights(localStorage.getItem('departure'),localStorage.getItem('departure'),signal)
      .then((data)=>{setFlights(data.flights)})
      .catch(err=>{
        if(err.name === "AbortError"){
          console.log('aborted')
        }
      })
      return () => {
        controller.abort();
      }
    }, [])


    // useEffect(() => {
    //   window.history.pushState(null, null, window.location.pathname);
    //   // window.addEventListener('popstate', onBackButtonEvent);
    // });
  return (
    <div className="flight-item-con">
      <div>Select Flight from {departure} to {arrival}</div>
      {flights.map((flight) => {
          return <Flightitem key={flight._id} flight={flight}/>
      })}
  </div>
  )
}

export default Searchflights
