import React,{useState} from 'react'
import { useNavigate } from 'react-router-dom'
import "../styles/Login.css";

const Login = () => {
    let navigate = useNavigate();
    const [credentials, setCredentials] = useState({email: "", password: ""})
    const [placeholder, setPlaceholder] = useState({"uname":"dnone", "pwd":"dnone"})
    const [height,setHeight] = useState({"uname":"ihide", "pwd":"ihide"})

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:4000/api/v1/auth/login", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({email: credentials.email, password: credentials.password})
        });
        const json = await response.json()
        console.log(json);
        if (json.success){
            // Save the auth token and redirect
            localStorage.setItem('token', json.authtoken); 
            navigate('/');

        }
        else{
            alert("Invalid credentials");
        }
    }

    const onChangelogin = (e) => {
      setCredentials({ ...credentials,[e.target.name]: e.target.value})
    }
  return (
    <div className='login-con'>
        <form className='login-form' onSubmit={handleSubmit}>
            <div className='login-f-title'>Login</div>
            <div className='login-input-item'>
                <div className={`login-placeholder ${placeholder.uname}`}>Email</div>
                <input className={`uname-input login-input ${height.uname}`} onFocus={(e)=> {
                        if(!e.target.value){
                            e.target.placeholder="" 
                            setPlaceholder({...placeholder,"uname":"dblock"})
                            setHeight({...height,"uname":"ishow"})
                        }
                    }
                } 
                onBlur={(e)=> {
                        if(e.target.value === ''){
                            e.target.placeholder="Email"
                            setPlaceholder({...placeholder,"uname":"dnone"})
                            setHeight({...height,"uname":"ihide"})
                        }
                    }
                } 
                placeholder='Email' onChange={onChangelogin}  value={credentials.email} type="text"  name="email" id="username"/>
            </div>
            <div className='login-input-item'>
                <div className={`login-placeholder ${placeholder.pwd}`}>Password</div>
                <input className={`pwd-input login-input ${height.pwd}`} onFocus={(e)=> {
                        if(!e.target.value){
                            e.target.placeholder="" 
                            setPlaceholder({...placeholder,"pwd":"dblock"})
                            setHeight({...height,"pwd":"ishow"})
                        }
                    }
                } 
                onBlur={(e)=> {
                        if(e.target.value === ''){
                            e.target.placeholder="Password"
                            setPlaceholder({...placeholder,"pwd":"dnone"})
                            setHeight({...height,"pwd":"ihide"})
                        }
                    }
                }   
                placeholder='Password'  onChange={onChangelogin}  value={credentials.password} type="text"  name="password"id="password"/>
            </div>
            <div className='login-btn'>Login</div>
        </form>
    </div>
  )
}

export default Login
