                        aria-expanded="false">
                        <div className="form-floating">
                          <input
                            className={`da-input da-input-l form-control `}
                            type="text"
                            onChange={onChangeDeparture}
                            value={searchflights[0].departuredisplay}
                            name="from"
                            id="from"
