import React,{useContext,useEffect,useState,useRef} from 'react'
import '../styles/BookingSummary.css'
import flightContent from "../context/FlightContext";
import '../styles/BookingSummary.css'
import { useNavigate } from 'react-router-dom';

const BookingSummary = () => {
    const host = "http://localhost:4000"
    const content = useContext(flightContent)
    const {bookingstatus, setBookingStatus,tripValue, settripValue,tripFlights,selectedflight, adultCounter, childCounter,infantCounter ,passCount,localpassengers,departDate, returnDate, passengersinfo, setPassengersinfo,setSelectedflight,contactinfo, setContactinfo} = content 


    useEffect(() => {
        settripValue(localStorage.getItem('triptype'))
        setSelectedflight(JSON.parse(localStorage.getItem('selectedflight')))
        setPassengersinfo(JSON.parse(localStorage.getItem('passengersinfo')))
        setContactinfo([JSON.parse(localStorage.getItem('contactinfo'))])
    }, [])


    const btn = useRef()
    const navigate = useNavigate()

    const [isBackButtonClicked, setIsBackButtonClicked] = useState(false)

    const onBackButtonEvent = (e) => {
      e.preventDefault();
      if(!isBackButtonClicked){
        btn.current.click();
      }
    }


    useEffect(() => {
      window.history.pushState(null, null, window.location.pathname);
      window.addEventListener('popstate', onBackButtonEvent);
    });

    const bookandpay= async () => {
        // const totalprice = tripFlights[0].economyprice+tripFlights[1].economyprice
        console.log(tripFlights,tripValue)
            const response = await fetch(`${host}/api/v1/wuflights`,{
                method:'POST',
                headers:{
                    'Content-type':'application/json',
                },
                body: JSON.stringify({ 
                                triptype: tripValue,
                                totalprice: tripValue === 'Return'?tripFlights[0].economyprice+tripFlights[1].economyprice:tripFlights[0].economyprice,
                                flightname:tripFlights[0].flightname,
                                flightnumber:tripFlights[0].flightnumber,
                                departure:tripFlights[0].departure,
                                departuretime:tripFlights[0].departuretime,
                                departureairport:tripFlights[0].departureairport,
