import React from 'react'
import '../styles/Components.css'
import Alert from './Alert'
import { useAuth } from '../context/Auth'


const Input = ({className,placeholder,onChange, value, type, name, id, validation}) => {

    const [auth, setAuth] = useAuth()


    return (
        <div>
            <div className="form-floating">
                <input 
                    className={`${className} form-control `}
                    placeholder={placeholder}
                    onChange={onChange}
                    value={value}
                    type={type}
                    name={name}
                    id={id}
                    onBlur={() => {
                            if(name !== 'cpassword'){
                                var pattern = new RegExp(validation);
                                let regex = pattern.test(value)
                                setAuth({...auth,[name]:regex})
                                // setInvalid({...invalid,[name]:regex ? '' : 'is-invalid'})
                            }
                            console.log(alert)
                        }
                    }
                />
                <label className='input-label-primary' htmlFor={id}>{placeholder}</label>
            </div>
            {auth === false ? <Alert error={placeholder} /> : <></>}
        </div>
    )
}

export default Input
