import React,{useContext, useState,useEffect} from 'react'
import '../styles/Passengerdetails.css'
import flightContent from "../context/FlightContext";
import Alert from './Alert';

const Passengerdetails = () => {
    const [credentials, setCredentials] = useState({title:"",firstname:"",lastname:"",dateofbirth:"",nationality:"",gender:"",passport:"",passportexpirydate:""})
    const [placeholder, setPlaceholder] = useState({"fname":"dnone", "lname":"dnone","dob":"dblock","nationality":"dnone","passport":"dnone","passportexpirydate":"dblock" })
    const [height,setHeight] = useState({"fname":"ihide", "lname":"ihide","dob":"ishow","nationality":"ihide","passport":"ihide","passportexpirydate":"ishow" })
    const onChangelogin = (e) => {
        setCredentials({ ...credentials,[e.target.name]: e.target.value})
      }

    const content = useContext(flightContent)
    const {selectedflight ,passarray, user, getUsersdata} = content 


    // retriving passenger details 
    const [passengers,setPassengers]= useState([])
    useEffect(() => {
        if(localStorage.getItem('token')){
            getUsersdata()
            getPassengers()
        }
        // eslint-disable-next-line
    }, [])
    const getPassengers = async () => {
        const data = await fetch("http://localhost:4000/api/v1/auth/getpassengers",{
            method:'GET',
            headers: {
            'Content-Type': 'application/json',
            'auth-token': localStorage.getItem('token')
            },
        })
        const passenger = await data.json()
        setPassengers(passenger.passenger)
        console.log(passenger)
    }

    // passenger drop-down
    const [pselected, setPselected] = useState("Select passenger");
    const onCP = (e) => {
        setPselected(e.target.getAttribute('data-value'))
        passengers.forEach((element)=>{
            if(element.firstname+element.lastname === e.target.getAttribute('data-value')){
                setCredentials({ ...credentials,"firstname":element.firstname,
                                                "lastname":element.lastname,})
            }
        })
    }

     // passenger validation alerts

     const [alert,setAlert] = useState({})
     const [alertborder, setAlertborder] = useState({"firstname":"", "lastname":"","dateofbirth":"","gender":"","state":""})



    //  passengers validation className={`${index===1?'':'accordion-button'}`}

    const [passvalidation,setPassvalidation] = useState(false)
    const [erroraccord, setErroraccord] = useState({})

    const accordClick = (e) => {
        if(!passvalidation){
            setErroraccord({...erroraccord,[e.target.getAttribute('data-value')]:true})
        }
        if(passvalidation){
            setCredentials({...credentials,firstname:''})
            setPassvalidation(false)
        }
    }
    // if(alert.firstname && alert.firstname !== ''){
    //     setPassvalidation(true)
    //     // setErroraccord({...erroraccord,[e.target.getAttribute('data-value')]:false})
    // }
    useEffect(() => {
        if(alert.firstname && credentials.firstname !== ''){
            console.log('hi')
            setPassvalidation(true)
            // setErroraccord({...erroraccord,[e.target.getAttribute('data-value')]:false})
        }
        // eslint-disable-next-line
    }, [credentials.firstname])

  return (
    <div className='passenger-d-con'>
        <div className='pd-title'>Passenger Details</div>
        <div className='d-flex justify-content-between'>
        <div className="accordion open" id="passengerAccordion">
        {passarray.map((element,index)=>{
          return    <div className="accordion-item">
                        <div className="pass-accord-header"  id={`heading${index=index+1}`} >
                        <button  onClick={accordClick} data-value={index} className='pass-accord-btn' type="button" data-bs-toggle="collapse" data-bs-target={passvalidation?`#collapse${index}`:''} aria-expanded='true' aria-controls={`collapse${index}`}>
                            <div className='pd-form-title'>Passenger {index} ({element})</div>
                        </button>
                        {erroraccord[index]?<div>Please complete the details of the remaining passengers in your booking to continue to the next step.</div>:<></>}
                        </div>
                        <div id={`collapse${index}`} className={`accordion-collapse collapse ${index===1?'show':''}`} aria-labelledby={`heading${index}`} data-bs-parent="#passengerAccordion">
                        <div className="accordion-body">
                        <form  className='pd-form'>
                                        <div>{localStorage.getItem('token')?
                                                <div className="dropdown">
                                                    <div className="btn btn-secondary dropdown-toggle" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                                        {pselected}
                                                    </div>
                                                    <div className="dropdown-menu">
                                                    {passengers.map((element)=>{
                                                    return <div className="dropdown-item" onClick={onCP} data-value={element.firstname+element.lastname}>{element.firstname+element.lastname}</div>
                                                    })}
                                                </div>
                                            </div>:<></>}
                                    </div>
                                    <div className='d-flex'>
                                        <div className='pd-input-item'>
                                            <div className={`pd-placeholder ${placeholder.fname}`}>First name</div>
                                            <input className={`fname-input pd-input ${height.fname}`} onFocus={(e)=> {
                                                    if(!e.target.value){
                                                        e.target.placeholder="" 
                                                        setPlaceholder({...placeholder,"fname":"dblock"})
                                                        setHeight({...height,"fname":"ishow"})
                                                    }
                                                }
                                            } 
                                            onBlur={(e)=> {
                                                let regex =/^[a-zA-Z]{1,}$/.test(credentials.firstname)
                                                setAlert({...alert,"firstname":regex})
                                                regex?setAlertborder({...alertborder,"firstname":''}):setAlertborder({...alertborder,"firstname":'alert-border'})
                                                    if(e.target.value === ''){
                                                        e.target.placeholder="First name"
                                                        setPlaceholder({...placeholder,"fname":"dnone"})
                                                        setHeight({...height,"fname":"ihide"})
                                                    }
                                                }
                                            } 
                                            placeholder='First name' onChange={onChangelogin}  value={credentials.firstname} type="text"  name="firstname" id="firstname"/>
                                        </div>
                                        <div className='pd-input-item'>
                                            <div className={`pd-placeholder ${placeholder.lname}`}>Last name</div>
                                            <input className={`lname-input pd-input ${height.lname}`} onFocus={(e)=> {
                                                    if(!e.target.value){
                                                        e.target.placeholder="" 
                                                        setPlaceholder({...placeholder,"lname":"dblock"})
                                                        setHeight({...height,"lname":"ishow"})
                                                    }
                                                }
                                            } 
                                            onBlur={(e)=> {
                                                    if(e.target.value === ''){
                                                        e.target.placeholder="Last name"
                                                        setPlaceholder({...placeholder,"lname":"dnone"})
                                                        setHeight({...height,"lname":"ihide"})
                                                    }
                                                }
                                            } 
                                            placeholder='Last name' onChange={onChangelogin}  value={credentials.lastname} type="text"  name="lastname" id="lastname"/>
                                        </div>
                                    </div>
                                    {alert.firstname === false?<Alert error={"Please enter your first name"}/>:<></>}
                                    <div className='d-flex'>
                                        <div className='pd-input-item'>
                                            <div className={`pd-placeholder ${placeholder.dob}`}>Date of birth</div>
                                            <input className={`dob-input pd-input ${height.dob}`} 
                                            placeholder='Date of birth' onChange={onChangelogin}  value={credentials.dateofbirth} type="date"  name="dateofbirth" id="dateofbirth"/>
                                        </div>
                                        <div className='pd-input-item'>
                                            <div className={`pd-placeholder ${placeholder.nationality}`}>Nationality</div>
                                            <input className={`dob-input pd-input ${height.nationality}`} onFocus={(e)=> {
                                                    if(!e.target.value){
                                                        e.target.placeholder="" 
                                                        setPlaceholder({...placeholder,"nationality":"dblock"})
                                                        setHeight({...height,"nationality":"ishow"})
                                                    }
                                                }
                                            } 
                                            onBlur={(e)=> {
                                                    if(e.target.value === ''){
                                                        e.target.placeholder="Nationality"
                                                        setPlaceholder({...placeholder,"nationality":"dnone"})
                                                        setHeight({...height,"nationality":"ihide"})
                                                    }
                                                }
                                            } 
                                            placeholder='Nationality' onChange={onChangelogin}  value={credentials.nationality} type="text"  name="dateofbirth" id="dateofbirth"/>
                                        </div>
                                    </div>
                                    <div className='select-radio-con'>
                                        <div className="form-check select-radio">
                                            <input className="form-check-input select-input" type="radio" name="genderOptions" id="genderOptions1"/>
                                            <label className="form-check-label select-label" htmlFor="genderOptions1">
                                                Male
                                            </label>
                                        </div>
                                        <div className="form-check select-radio">
                                            <input className="form-check-input select-input" type="radio" name="genderOptions" id="genderOptions2"/>
                                            <label className="form-check-label select-label" htmlFor="genderOptions2">
                                                Female
                                            </label>
                                        </div>
                                    </div>
                                    <div className='d-flex'>
                                        <div className='pd-input-item'>
                                            <div className={`pd-placeholder ${placeholder.passport}`}>Passport Details(Optional)</div>
                                            <input className={`passport-input pd-input ${height.passport}`} onFocus={(e)=> {
                                                    if(!e.target.value){
                                                        e.target.placeholder="" 
                                                        setPlaceholder({...placeholder,"passport":"dblock"})
                                                        setHeight({...height,"passport":"ishow"})
                                                    }
                                                }
                                            } 
                                            onBlur={(e)=> {
                                                    if(e.target.value === ''){
                                                        e.target.placeholder="Passport Details(Optional)"
                                                        setPlaceholder({...placeholder,"passport":"dnone"})
                                                        setHeight({...height,"passport":"ihide"})
                                                    }
                                                }
                                            } 
                                            placeholder='Passport Details(Optional)' onChange={onChangelogin}  value={credentials.passport} type="text"  name="passport" id="passport"/>
                                        </div>
                                        <div className='pd-input-item'>
                                            <div className={`pd-placeholder ${placeholder.passportexpirydate}`}>Passport expirydate</div>
                                            <input className={`passportexpirydate-input pd-input ${height.passportexpirydate}`}  
                                            placeholder='Passport expirydate' onChange={onChangelogin}  value={credentials.expirydate} type="date"  name="passportexpirydate" id="passportexpirydate"/>
                                        </div>
                                    </div>
                                    </form>
                        </div>
                        </div>
                    </div>
        })}
    </div>
            <div className='trip-summary-con'>
                <div className='ts-title'>Your trip summary</div>
                {selectedflight.map((flight)=>{
                        return                         <div className='fd-con'>
                        <div className='ts-body'>
                            <div className='ts-item'>Outbound flight</div>
                            <div className='d-flex justify-content-between'>
                                <div className='ts-item'>{flight.departuredate}</div>
                                <div className='ts-item'>{flight.arrivaldate}</div>
                            </div>
                            <div className='d-flex'>
                                <div>
                                    <div className='ts-item'>{flight.from}</div>
                                    <div className='ts-item'>{flight.departuretime}</div>
                                </div>
                                <div className='d-flex align-items-center'>
                                    <div className='flightline'></div>
                                </div>
                                <div>
                                    <div className='ts-item'>{flight.to}</div>
                                    <div className='ts-item'>{flight.arrivaltime}</div>
                                </div>
                            </div>
                            <div className='ts-item'>Opearted by {flight.flightname}</div>
                        </div>
                        <div className='ts-footer'>
                            <div className='ts-item'>Total trip price:</div>
                            <div className='ts-item'>{flight.price}</div>
                        </div>
                </div>
                    })}
            </div>
        </div>
    </div>
  )
}

export default Passengerdetails
