    const {searchflights, tripFlights, alert, setAlert, invalid, setInvalid,
        localpassengers, setLocalPassengers
    } = useFlight()
    const {auth, setAuth} = useAuth()

    const updatePassengerDetails = (index, value) => {
        const upd = localpassengers.map((c, i) => {
