import React,{useState,useContext,useEffect} from "react";
import "../styles/Home.css";
import flightContent from "../context/FlightContext";
import { useNavigate } from "react-router-dom";
import Alert from './Alert';



const Home = () => {

  var navigate = useNavigate()
  const [alert,setAlert] = useState({})

  // Departure-arrival
  const content = useContext(flightContent)
  const {departure, setDeparture, setArrival, arrival, filter, filterDest, onChangeDeparture, onChangeArrival ,getFlights, departDate, setSearch ,tripValue, settripValue , setdepartDate ,adultCounter, setadultCounter,childCounter, setchildCounter, infantCounter, setinfantCounter, passCount, setPassCount, pclass, setPclass, onChange, passarray, setPassarray} = content 
  const [placeholder, setPlaceholder] = useState({"from":"dnone", "to":"dnone"})
  const [height,setHeight] = useState({"from":"ihide", "to":"ihide"})
  const [selected,setSeleceted] = useState(false)

  // Search for flights
  const SearchFlights = () => {
    if(departure && arrival && departDate){
      const pa = []
      for(let i=0; i<adultCounter; i++){pa.push('Adult')}
      for(let i=0; i<childCounter; i++){pa.push('Child')}
      for(let i=0; i<infantCounter; i++){pa.push('Infant')}
      setPassarray(pa)
      setSearch('true')
      getFlights(departure,arrival,departDate)
      navigate('/search')
    }
    else{
      if(departure === ''){setAlert({departure:"Departure airport is missing"})}
      if(arrival === ''){setAlert({...alert,arrival:'Destination airport is missing'})}
      if(departDate === ''){setAlert({...alert,date:'Departure date is missing'}) }
      setAlert({...alert,success:true})
    }
  }
  // useEffect(() => {
  //   if(departure){
  //     setPlaceholder({...placeholder,"from":"dblock"})
  //     setHeight({...height,"from":"ishow"})
  //   }
  //   // if(departure===''){
  //   //   setPlaceholder({...placeholder,"from":"dnone"})
  //   //   setHeight({...height,"from":"ihide"})
  //   // }
  // }, [departure])


  return (
    <>
    <div className="search-flights-con">
      <form className="search-flights-form">
        {alert.success?<Alert alert={alert}/>:<></>}
        <div className="f-q-container  d-flex">
          {/* to and from */}
          <div className="desti-con d-flex">
            <div className="dropdown da-select departure-select">
                <div className='dropdown-toggle da-dropdown-btn departure-drop-btn' data-bs-toggle="dropdown" aria-expanded="false">
                    <div className={`da-placeholder ${placeholder.from}`}>From</div>
                    <div>
                        <input className={`departure-input da-input ${height.from}`} 
                          onFocus={(e)=> {
                              if(selected){
                                setPlaceholder({...placeholder,"from":"dblock"})
                                setHeight({...height,"from":"ishow"})
                              }
                              if(!e.target.value){
                                  e.target.placeholder="" 
                                  setPlaceholder({...placeholder,"from":"dblock"})
                                  setHeight({...height,"from":"ishow"})
                              }
                            }
                          } 
                          onBlur={(e)=> {
                                  if(!selected){
                                    console.log('not selected')
                                    setDeparture('')
                                    filterDest('')
                                  }
                                  if(!departure){
                                      console.log('empty')
                                      e.target.placeholder="From"
                                      setPlaceholder({...placeholder,"from":"dnone"})
                                      setHeight({...height,"from":"ihide"})
                                  }
                                  if(departure){
                                    e.target.placeholder="" 
                                    setPlaceholder({...placeholder,"from":"dblock"})
                                    setHeight({...height,"from":"ishow"})
                                  }
                              }
                          }  
                          placeholder="From" onChange={onChangeDeparture}  value={departure} type="text"  name="from"  required/>
                    </div>
                </div>
              <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
                return  <div key={element.id} className='departure-item' 
                          onClick={(e) => {  
                              e.preventDefault()
                              setDeparture(element.id)
                              filterDest('')
                              setSeleceted(true)
                            }
                          }
                          >
                            <div  className='d-flex justify-content-between align-items-center'>
                                <div className='d-city'>{element.city}</div>
                                <div className='d-code'>{element.id}</div>
                            </div>
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-country'>{element.country}</div>
                                <div className='d-name'>{element.name}</div>
                            </div>
                        </div> }) }
              </div>
            </div>
            <div className="dropdown da-select arrival-select">
                <div className='dropdown-toggle da-dropdown-btn' data-bs-toggle="dropdown" aria-expanded="false">
                    <div className='da-placeholder'>To</div>
                    <div>
                    <input className={`departure-input da-input ${height.to}`} 
                          onFocus={(e)=> {
                              if(!e.target.value){
                                  e.target.placeholder="" 
                                  setPlaceholder({...placeholder,"to":"dblock"})
                                  setHeight({...height,"to":"ishow"})
                              }
                            }
                          } 
                          onBlur={(e)=> {
                                  if(e.target.value === ''){
                                      e.target.placeholder="To"
                                      setPlaceholder({...placeholder,"to":"dnone"})
                                      setHeight({...height,"to":"ihide"})
                                  }
                              }
                          }  
                          placeholder="To" onChange={onChangeArrival} type="text" value={arrival}  name="to" required />
                    </div>
                </div>
              <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
                return  <div key={element.id} className='departure-item' onClick={(e) => {  e.preventDefault()
                setArrival(element.id)
                filterDest('')}} >
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-city'>{element.city}</div>
                                <div className='d-code'>{element.id}</div>
                            </div>
                            <div className='d-flex justify-content-between align-items-center'>
                                <div className='d-country'>{element.country}</div>
                                <div className='d-name'>{element.name}</div>
                            </div>
                        </div> }) }
              </div>
            </div>
          </div>
          {/* Trip category */}
          <div className="dropdown trip-details-con">
            <div className="trip-details-btn dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                <div className="trip-placeholder">Trip</div>
                <div className="trip-displayvalue">{tripValue}</div>
            </div>
            <div className="dropdown-menu trip-select-con">
                <div className="trip-item" onClick={()=> settripValue('One way')}>One-way</div>
                <div className="trip-item" onClick={()=> settripValue('Return')}>Return</div>
            </div>
          </div>
          {/* Trip dates */}
          <div className="date-select-con d-flex">
            <div className="depart date-select-item">
                <div className="date-placeholder">Depart</div>
                <input className="date-displayvalue departure" value={departDate} onChange={(e) => setdepartDate(e.target.value)} type="date" id="departure" name="departure"></input>
            </div>
            {tripValue === 'Return'? <div className="return date-select-item">
              <div className="date-placeholder">Return</div>
              <input className="date-displayvalue arrival" type="date" id="return" name="return"></input>
            </div>:<div></div>}
          </div>
          {/* Passenger Count */}
          <div className="dropdown passengercount-con">
            <div className="dropdown-toggle pc-dropdown-btn" data-bs-toggle="dropdown" aria-expanded="false" data-bs-auto-close="outside">
              <div className="pc-placeholder">Passenger / Class</div>
              <div className="pc-displayvalue">
                {passCount} Passenger, {pclass}
              </div>
            </div>
            <div className="dropdown-menu pass-class-con">
              <div className="pass-detail-con">
                <div className="p-d-heading">Passenger</div>
                <div className="d-flex justify-content-between">
                  <div className="p-holder">Adult (12years+)</div>
                  <div className="d-flex counter-con">
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount > 1) {
                          setadultCounter((adultCounter) => adultCounter - 1);
                          setPassCount((passCount) => passCount - 1);
                          setPassarray([...passarray,''])
                        }
                      }}> -
                    </div>
                    <div className="p-value p-a-value">{adultCounter}</div>
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount < 9) {
                          setadultCounter((adultCounter) => adultCounter + 1);
                          setPassCount((passCount) => passCount + 1);
                        }
                      }}>
                      +
                    </div>
                  </div>
                </div>
                <div className="d-flex justify-content-between">
                  <div className="p-holder">Child (2-11 years)</div>
                  <div className="d-flex counter-con">
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount > 1 && childCounter > 0) {
                          setchildCounter((childCounter) => childCounter - 1);
                          setPassCount((passCount) => passCount - 1);
                        }
                      }}>
                      -
                    </div>
                    <div className="p-value p-c-value">{childCounter}</div>
                    <div
                      className="p-counter"
                      onClick={() => {
                        if (passCount < 9) {
                          setchildCounter((childCounter) => childCounter + 1);
                          setPassCount((passCount) => passCount + 1);
                        }
                      }}>
                      +
                    </div>
                  </div>
                </div>
                <div className="d-flex justify-content-between mb-3">
                  <div className="p-holder">Infant (Under 2 years)</div>
                  <div className="d-flex counter-con">
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount > 1 && infantCounter > 0) {
                          setinfantCounter((infantCounter) => infantCounter - 1);
                          setPassCount((passCount) => passCount - 1 );
                        }
                      }}>
                      -
                    </div>
                    <div className="p-value p-i-value">{infantCounter}</div>
                    <div className="p-counter"
                      onClick={() => {
                        if (passCount < 9) {
                          setinfantCounter((infantCounter) => infantCounter + 1);
                          setPassCount((passCount) => passCount + 1
                          );
                        }
                      }}>
                      +
                    </div>
                  </div>
                </div>
                <div className="class-con">
                  <div>
                    <label htmlFor="ecomony">Ecomony</label>
                    <input type="radio" onChange={onChange} id="ecomomy" name="classelection" value="Economy" checked />
                    <label htmlFor="premeium">Premiuem</label>
                    <input type="radio" onChange={onChange} id="premieum" name="classelection" value="Premiuem" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="s-b-con">
          <button type="button" onClick={SearchFlights}className="search-flights-btn">Search Flights</button>
        </div>
      </form>
    </div>
    </>
  )
}

export default Home;
