import React,{useState} from 'react'
import { useNavigate } from 'react-router-dom'
import "../styles/Login.css";

const Login = () => {
    let navigate = useNavigate();
    const [credentials, setCredentials] = useState({email: "", password: ""})

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:4000/api/v1/auth/login", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({email: credentials.email, password: credentials.password})
        });
        const json = await response.json()
        console.log(json);
        if (json.success){
            // Save the auth token and redirect
            localStorage.setItem('token', json.authtoken); 
            navigate('/');

        }
        else{
            alert("Invalid credentials");
        }
    }

    const onChangelogin = (e) => {
      setCredentials({ ...credentials,[e.target.name]: e.target.value})
    }
  return (
    <div>
        <form className='login-form' onSubmit={handleSubmit}>
            <div>
                <input className="uname-input login-input"  onChange={onChangelogin}  value={credentials.email} type="text"  name="email" id="username"/>
            </div>
            <div>
                 <input className="pwd-input login-input"  onChange={onChangelogin}  value={credentials.password} type="text"  name="password"id="password"/>
            </div>
            <button>Login</button>
        </form>
    </div>
  )
}

export default Login
