import React,{useState,useContext} from "react";
import "../styles/Home.css";

import Passengercount from "./Passengercount";
import Flightitem from "./Flightitem";
import flightContent from "../context/FlightContext";
const Home = () => {

  const Airports = [
    {
        name:"Netaji Subhash Chandra Bose International Airport",
        city:"Kolkata",
        id:"CCA",
        country:"India"
    },
    {
        name:"Chennai International Airport",
        city:"Chennai",
        id:"CHE",
        country:"India"
    },
    {
        name:"Thiruvananthapuram International Airport",
        city:"Thiruvananthapuram",
        id:"CCC",
        country:"India"
    },
    {
        name:"Sardar Vallabh Bhai Patel International Airport",
        city:"Ahmedabad",
        id:"CCD",
        country:"India"
    },
    {
        name:"Guru Ram Dass Jee International Airport",
        city:"Amritsar",
        id:"CCE",
        country:"India"
    },
    {
        name:"Lokpriya Gopinath Bordoloi International Airport",
        city:"Guwahati",
        id:"CCF",
        country:"India"
    },
    {
        name:"Goa International Airport",
        city:"Goa",
        id:"CCJ",
        country:"India"
    },
    {
        name:"Srinagar International Airport",
        city:"Srinagar",
        id:"CCH",
        country:"India"
    }, 
    {
        name:"Jaipur International Airport",
        city:"Jaipur",
        id:"CCI",
        country:"India"
    },
    {
        name:"Kozhikode Airport",
        city:"Calicut",
        id:"CCZ",
        country:"India"
    },
    {
        name:"Veer Savarkar International Airport",
        city:" Port Blair, A&N Islands",
        id:"CCK",
        country:"India"
    },
    {
        name:"Indira Gandhi International Airport",
        city:"Delhi",
        id:"DEL",
        country:"India"
    },
    {
        name:"Chattrapati Shivaji International Airport",
        city:"Mumbai",
        id:"CCM",
        country:"India"
    },
    {
        name:"GMR Hyderabad International Airport",
        city:"Hyderabad",
        id:"HYD",
        country:"India"
    },
    {
        name:"Bangalore International Airport Limited",
        city:"Bengaluru",
        id:"BAN",
        country:"India"
    },
    {
        name:"Cochin International Airport",
        city:"Kochi",
        id:"CCP",
        country:"India"
    },
    {
        name:"Bharat Ratna Babasaheb Dr. B.R. Ambedkar International Airport",
        city:"Nagpur",
        id:"CCQ",
        country:"India"
    },

]
const [departure, setDeparture] = useState('')
const [arrival, setArrival] = useState('')
const [filter, setFilter] = useState(Airports)
const onChangeArrival = (e) => {
    setArrival(e.target.value)
    filterDest(e.target.value)
}
const onChangeDeparture = (e) => {
    setDeparture(e.target.value)
    filterDest(e.target.value)
}
const filterDest = (value) =>{
    const result = Airports.filter((element)=> {
        const {name,city} = element
        var cityfilter = city.toLowerCase()
        var namefilter = name.toLowerCase()
        if(cityfilter.indexOf(value.toLowerCase()) > -1 || namefilter.indexOf(value.toLowerCase()) > -1){
            return element
        }
    })
    setFilter(result)
}



    const [tripValue, settripValue] = useState('One way')
    const content = useContext(flightContent)
    const {flights,getFlights} = content 
    const [departDate,setdepartDate]=useState('')

  const SearchFlights = () => {
      getFlights(departure,arrival,departDate)
  }

  return (
    <>
    <div className="search-flights-con">
        <form>
          <div className="f-q-container  d-flex">
          <div className="desti-con d-flex">
    <div className="dropdown departure-select">
        {/* {   departure === '' ?
             <input className="dropdown-toggle departure-input-intial" data-bs-toggle="dropdown" aria-expanded="false" onChange={onChangeDeparture}  value={departure} type="text"  name="from"  placeholder='From'/>
             :
             <div className='dropdown-toggle da-dropdown-btn' data-bs-toggle="dropdown" aria-expanded="false">
                <div className='da-placeholder'>From</div>
                <div>
                    <input className="departure-input"  onChange={onChangeDeparture}  value={departure} type="text"  name="from"  />
                </div>
            </div>
        } */}
        <div className='dropdown-toggle da-dropdown-btn' data-bs-toggle="dropdown" aria-expanded="false">
            <div className='da-placeholder'>From</div>
            <div>
                <input className="departure-input da-input"  onChange={onChangeDeparture}  value={departure} type="text"  name="from"  />
            </div>
        </div>
      <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
        return  <div key={element.id} className='departure-item' onClick={(e) => {  e.preventDefault()
         setDeparture(element.id)
         filterDest('')}} >
                    <div  className='d-flex justify-content-between align-items-center'>
                        <div className='d-city'>{element.city}</div>
                        <div className='d-code'>{element.id}</div>
                    </div>
                    <div className='d-flex justify-content-between align-items-center'>
                        <div className='d-country'>{element.country}</div>
                        <div className='d-name'>{element.name}</div>
                    </div>
                </div> }) }
      </div>
    </div>
    <div className="dropdown arrival-select">
        <div className='dropdown-toggle da-dropdown-btn' data-bs-toggle="dropdown" aria-expanded="false">
            <div className='da-placeholder'>To</div>
            <div>
            <input className="arrival-input da-input" onChange={onChangeArrival} type="text" value={arrival}  name="to" />
            </div>
        </div>
      <div className="dropdown-menu ad-options">{ filter.map((element)=> { 
        return  <div key={element.id} className='departure-item' onClick={(e) => {  e.preventDefault()
         setArrival(element.id)
         filterDest('')}} >
                    <div className='d-flex justify-content-between align-items-center'>
                        <div className='d-city'>{element.city}</div>
                        <div className='d-code'>{element.id}</div>
                    </div>
                    <div className='d-flex justify-content-between align-items-center'>
                        <div className='d-country'>{element.country}</div>
                        <div className='d-name'>{element.name}</div>
                    </div>
                </div> }) }
      </div>
    </div>

  </div>
            <div className="dropdown trip-details-con">
              <div className="trip-details-btn dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                <div className="trip-placeholder">Trip</div>
                <div className="trip-displayvalue">{tripValue}</div>
              </div>
              <div className="dropdown-menu trip-select-con">
                <div className="trip-item" onClick={()=> settripValue('One way')}>One-way</div>
                <div className="trip-item" onClick={()=> settripValue('Return')}>Return</div>
              </div>
            </div>
            <div className="date-select-con d-flex">
             <div className="depart date-select-item">
                  <div className="date-placeholder">Depart</div>
                  <input className="date-displayvalue departure" value={departDate} onChange={(e) => setdepartDate(e.target.value)} type="date" id="departure" name="departure"></input>
              </div>
              {tripValue === 'Return'? <div className="return date-select-item">
                <div className="date-placeholder">Return</div>
                <input className="date-displayvalue arrival" type="date" id="return" name="return"></input>
              </div>:<div></div>}
            </div>

            <Passengercount/>
          </div>
          <div className="s-b-con">
            <button type="button" onClick={SearchFlights}className="search-flights-btn">Search Flights</button>
          </div>
        </form>
    </div>
        <div className="flight-item-con">
          { flights.map((flight) => {
             return <Flightitem key={flight._id} flight={flight}/>
          })}
        </div>
    </>
  );
};

export default Home;
