import React,{useState} from 'react'
import '../styles/Components.css'
import Alert from './Alert'


const Input = ({className,placeholder,onChange, value, type, name, id, validation}) => {

    const [alert, setAlert] = useState({})
    const [invalid, setInvalid] = useState({})


    return (
        <div>
            <div className="form-floating">
                <input 
                    className={`${className} form-control ${invalid+'.'+name}`}
                    placeholder={placeholder}
                    onChange={onChange}
                    value={value}
                    type={type}
                    name={name}
                    id={id}
                    onBlur={() => {
                            if(name !== 'cpassword'){
                                var pattern = new RegExp(validation);
                                let regex = pattern.test(value)
                                setAlert({ ...alert, name : regex })
                                setInvalid({ ...invalid, name : regex ? '' : 'is-invalid' })
                            }
                        }
                    }
                />
                <label className='input-label-primary' htmlFor={id}>{placeholder}</label>
            </div>
            {alert+'.'+name === false ? <Alert error={placeholder} /> : <></>}
        </div>
    )
}

export default Input
