import React,{useState} from 'react'
import { useNavigate,Link } from 'react-router-dom'
const Register = () => {
  let navigate = useNavigate();
  const [credentials, setCredentials] = useState({"name":"","email":"","password":""})
  const [placeholder, setPlaceholder] = useState({"name":"dnone","uname":"dnone", "pwd":"dnone"})
  const [height,setHeight] = useState({"name":"ihide","uname":"ihide", "pwd":"ihide"})

  const onChangelogin = (e) => {
      setCredentials({...credentials,[e.target.name]:e.target.value})
  } 

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch ("http://localhost:4000/api/v1/auth",{
      method:'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({name: credentials.name, email: credentials.email, password: credentials.password})
    })
    const json = await response.json()
    if (json.success){
      // Save the auth token and redirect
      localStorage.setItem('token', json.authtoken); 
      navigate('/');
    }
    else{
      alert('try again')
    }
  }


  return (
    <div className='lr-con'>
    <form className='lr-form' onSubmit={handleSubmit}>
        <h1 className='lr-f-title'>Register</h1>
        <div className='lr-input-item'>
            <div className={`lr-placeholder ${placeholder.name}`}>Name</div>
            <input className={`uname-input lr-input ${height.name}`} onFocus={(e)=> {
                    if(!e.target.value){
                        e.target.placeholder="" 
                        setPlaceholder({...placeholder,"name":"dblock"})
                        setHeight({...height,"name":"ishow"})
                    }
                }
            } 
            onBlur={(e)=> {
                    if(e.target.value === ''){
                        e.target.placeholder="Enter your email address"
                        setPlaceholder({...placeholder,"name":"dnone"})
                        setHeight({...height,"name":"ihide"})
                    }
                }
            } 
            placeholder='Enter your name' onChange={onChangelogin}  value={credentials.name} type="text"  name="name" id="name"/>
        </div>
        <div className='lr-input-item'>
            <div className={`lr-placeholder ${placeholder.uname}`}>Email</div>
            <input className={`uname-input lr-input ${height.uname}`} onFocus={(e)=> {
                    if(!e.target.value){
                        e.target.placeholder="" 
                        setPlaceholder({...placeholder,"uname":"dblock"})
                        setHeight({...height,"uname":"ishow"})
                    }
                }
            } 
            onBlur={(e)=> {
                    if(e.target.value === ''){
                        e.target.placeholder="Enter your email address"
                        setPlaceholder({...placeholder,"uname":"dnone"})
                        setHeight({...height,"uname":"ihide"})
                    }
                }
            } 
            placeholder='Enter your email address' onChange={onChangelogin}  value={credentials.email} type="text"  name="email" id="username"/>
        </div>
        <div className='lr-input-item'>
            <div className={`lr-placeholder ${placeholder.pwd}`}>Password</div>
            <input className={`pwd-input lr-input ${height.pwd}`} onFocus={(e)=> {
                    if(!e.target.value){
                        e.target.placeholder="" 
                        setPlaceholder({...placeholder,"pwd":"dblock"})
                        setHeight({...height,"pwd":"ishow"})
                    }
                }
            } 
            onBlur={(e)=> {
                    if(e.target.value === ''){
                        e.target.placeholder="Enter your password"
                        setPlaceholder({...placeholder,"pwd":"dnone"})
                        setHeight({...height,"pwd":"ihide"})
                    }
                }
            }   
            placeholder='Enter your password'  onChange={onChangelogin}  value={credentials.password} type="text"  name="password"id="password"/>
        </div>
        <button className='lr-btn'>Register</button>
    </form>
    <div className='lr-link-con'>
        <div className='lr-context'>Have an account already?</div>
        <Link to="/login" className='lr-btn'>Login</Link>
    </div>
</div>
  )
}

export default Register
