import React,{useState} from 'react'
import '../styles/Components.css'
import Alert from './Alert'


const Input = ({className,placeholder,onChange, value, type, name, id}) => {

    const [alert, setAlert] = useState({})
    const [invalid, setInvalid] = useState({})

    return (
        <div>
            <div className="form-floating">
                <input 
                    className={`${className} form-control ${invalid.email}`}
                    placeholder={placeholder}
                    onChange={onChange}
                    value={value}
                    type={type}
                    name={name}
                    id={id}
                    onBlur={() => {
                            let regex = /@/.test(value)
                            setAlert({ ...alert, "email": regex })
                            setInvalid({ ...invalid, 'email': regex ? '' : 'is-invalid' })
                        }
                    }
                />
                <label className='cd-innput-label' htmlFor="email">{placeholder}</label>
            </div>
            {alert.email === false ? <Alert error={"Enter a valid email address"} /> : <></>}
        </div>
    )
}

export default Input
