import React,{ createContext, useContext, useState , useEffect} from 'react'
import axios from 'axios'

const FlightContent = createContext()


const FlightProvider = (props) => {

  const Airports = [
    {
        "name":"Kempegowda International Airport",
        "city":"Bengaluru",
        "id":"BLR",
        "country":"India"
    },
    {
        "name":"Chennai International Airport",
        "city":"Chennai",
        "id":"CHE",
        "country":"India"
    },
    {
        "name":"Indira Gandhi International Airport",
        "city":"Delhi",
        "id":"DEL",
        "country":"India"
    },
    {
        "name":"Rajiv Gandhi International Airport",
        "city":"Hyderabad",
        "id":"HYD",
        "country":"India"
    },
    {
        "name":"Netaji Subhash Chandra Bose International Airport",
        "city":"Kolkata",
        "id":"CCU",
        "country":"India"
    },
    {
        "name":"Chattrapati Shivaji Maharaj International Airport",
        "city":"Mumbai",
        "id":"BOM",
        "country":"India"
    }

]
 // Home
//  const [searchdate, setSearchDate] = useState()
//  const [search,setSearch] = useState('')
//  const [pdinitial,setPdInitial] = useState([{}])
//  const[passarray,setPassarray] = useState([])

 const[searchflights, setSearchFlights] = useState([
  {
    'departure':'',
    'departurecode':''
  },
  {
    'arrival':'',
    'arrivalcode':''
  },
  {
    'tripValue':'One-way'
  },
  null,
  {},
  {
      'passengerCount':1,
      'adultCount':1,
      'childCount':0,
      'infantCount':0,
      'passengerClass':'Economy'
  },
])

const updateSearchFlights = (index, value) => {
  const utf = searchflights.map((c, i) => {
      if (i === index) {
        return value ;
      } else {
        return c;
      }
  });
  setSearchFlights(utf);
}
// const [condition, setCondition] = useState(0)
  // const [alert,setAlert] = useState({})
  // const [invalid, setInvalid] = useState({})


  // return (
  //   <FlightContent.Provider 
  //       value={[Airports ,searchdate, setSearchDate,search,setSearch,searchflights,pdinitial,setPdInitial,passarray,setPassarray,
  //               setSearchFlights, updateSearchFlights,condition, setCondition ]}>
  //       {props.children}
  //   </FlightContent.Provider>
  // )
  return (
    <FlightContent.Provider value={[Airports ,searchflights, updateSearchFlights ]}>
        {props.children}
    </FlightContent.Provider>
  )
}

const useFlight = () => useContext(FlightContent)

export { useFlight, FlightProvider}
