import React,{useState} from 'react'
import '../styles/Home.css'

const Destination = () => {


    const Airports = [
        {
            name:"Netaji Subhash Chandra Bose International Airport",
            city:"Kolkata"
        },
        {
            name:"Chennai International Airport",
            city:"Chennai"
        },
        {
            name:"Thiruvananthapuram International Airport",
            city:"Thiruvananthapuram"
        },
        {
            name:"Sardar Vallabh Bhai Patel International Airport",
            city:"Ahmedabad"
        },
        {
            name:"Guru Ram Dass Jee International Airport",
            city:"Amritsar"
        },
        {
            name:"Lokpriya Gopinath Bordoloi International Airport",
            city:"Guwahati"
        },
        {
            name:"Goa International Airport",
            city:"Goa"
        },
        {
            name:"Srinagar International Airport",
            city:"Srinagar"
        }, 
        {
            name:"Jaipur International Airport",
            city:"Jaipur"
        },
        {
            name:"Kozhikode Airport",
            city:"Calicut"},
        {
            name:"Veer Savarkar International Airport",
            city:" Port Blair, A&N Islands"
        },
        {
            name:"Indira Gandhi International Airport",
            city:"Delhi"
        },
        {
            name:"Chattrapati Shivaji International Airport",
            city:"Mumbai"
        },
        {
            name:"GMR Hyderabad International Airport",
            city:"Hyderabad"
        },
        {
            name:"Bangalore International Airport Limited",
            city:"Bengaluru"
        },
        {
            name:"Cochin International Airport",
            city:"Kochi"
        },
        {
            name:"Bharat Ratna Babasaheb Dr. B.R. Ambedkar International Airport",
            city:"Nagpur"
        },

    ]
    const [departure, setDeparture] = useState('')
    const [arrival, setArrival] = useState('')
    const [filter, setFilter] = useState(Airports)
    const onChange = (e) => {
        const result = Airports.filter((element)=> {
            const {name,city} = element
            var cityfilter = city.toLowerCase()
            var namefilter = name.toLowerCase()
            if(cityfilter.indexOf(e.target.value) > -1 || namefilter.indexOf(e.target.value) > -1){
                return name+city
            }
        })
        setFilter(result)
    }

    console.log(departure)
  return (
    <div className="desti-con d-flex">
    <div className="dropdown mx-2">
      <input className=" dropdown-toggle" onChange={onChange('departure')} type="text" value={departure} name="from" data-bs-toggle="dropdown" aria-expanded="false" placeholder="From"/>
      <div className="dropdown-menu departure-options">{ filter.map((element)=> { return  <div className='departure-item' onClick={() => setDeparture(element.name+'-'+element.name)} >{element.name}-{element.city}</div> }) }
      </div>
    </div>
    <div className="dropdown">
      <input className="dropdown-toggle" onChange={onChange('arrival')} type="text" value={arrival}  name="to" data-bs-toggle="dropdown" aria-expanded="false" placeholder="To"
      />
      <div className="dropdown-menu">{ filter.map((element)=> { return  <div className='arrival-item' onClick={() => setArrival(element.name+'-'+element.name)} >{element.name}-{element.city}</div> }) }
      </div>
    </div>

  </div>
  )
}

export default Destination
